lse>\n        <text variable=\"title\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"publisher\">\n    <choose>\n      <if type=\"report\" match=\"any\">\n        <group delimiter=\": \">\n          <text variable=\"publisher-place\"/>\n          <text variable=\"publisher\"/>\n        </group>\n      </if>\n      <else-if type=\"thesis\" match=\"any\">\n        <group delimiter=\", \">\n          <text variable=\"publisher\"/>\n          <text variable=\"publisher-place\"/>\n        </group>\n      </else-if>\n      <else>\n        <group delimiter=\", \">\n          <choose>\n            <if variable=\"event\" match=\"none\">\n              <text variable=\"genre\"/>\n            </if>\n          </choose>\n          <choose>\n            <if type=\"article-journal article-magazine\" match=\"none\">\n              <group delimiter=\": \">\n                <text variable=\"publisher-place\"/>\n                <text variable=\"publisher\"/>\n              </group>\n            </if>\n          </choose>\n        </group>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"event\">\n    <choose>\n      <if variable=\"event\">\n        <choose>\n          <if variable=\"genre\" match=\"none\">\n            <text term=\"presented at\" text-case=\"capitalize-first\" suffix=\" \"/>\n            <text variable=\"event\"/>\n          </if>\n          <else>\n            <group delimiter=\" \">\n              <text variable=\"genre\" text-case=\"capitalize-first\"/>\n              <text term=\"presented at\"/>\n              <text variable=\"event\"/>\n            </group>\n          </else>\n        </choose>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"issued\">\n    <choose>\n      <if type=\"bill legal_case legislation\" match=\"none\">\n        <choose>\n          <if variable=\"issued\">\n            <group prefix=\" (\" suffix=\")\">\n              <date variable=\"issued\">\n                <date-part name=\"year\"/>\n              </date>\n              <text variable=\"year-suffix\"/>\n              <choose>\n                <if type=\"article-journal bill book chapter graphic legal_case legislation motion_picture paper-conference report song\" match=\"none\">\n                  <date variable=\"issued\">\n                    <date-part prefix=\", \" name=\"month\"/>\n                    <date-part prefix=\" \" name=\"day\"/>\n                  </date>\n                </if>\n              </choose>\n            </group>\n          </if>\n          <else>\n            <group prefix=\" (\" suffix=\")\">\n              <text term=\"no date\" form=\"short\"/>\n              <text variable=\"year-suffix\" prefix=\"-\"/>\n            </group>\n          </else>\n        </choose>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"issued-sort\">\n    <choose>\n      <if type=\"article-journal bill book chapter graphic legal_case legislation motion_picture paper-conference report song\" match=\"none\">\n        <date variable=\"issued\">\n          <date-part name=\"year\"/>\n          <date-part name=\"month\"/>\n          <date-part name=\"day\"/>\n        </date>\n      </if>\n      <else>\n        <date variable=\"issued\">\n          <date-part name=\"year\"/>\n        </date>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"issued-year\">\n    <choose>\n      <if variable=\"issued\">\n        <date variable=\"issued\">\n          <date-part name=\"year\"/>\n        </date>\n        <text variable=\"year-suffix\"/>\n      </if>\n      <else>\n        <text term=\"no date\" form=\"short\"/>\n        <text variable=\"year-suffix\" prefix=\"-\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"edition\">\n    <choose>\n      <if is-numeric=\"edition\">\n        <group delimiter=\" \">\n          <number variable=\"edition\" form=\"ordinal\"/>\n          <text term=\"edition\" form=\"short\"/>\n        </group>\n      </if>\n      <else>\n        <text variable=\"edition\" suffix=\".\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"locators\">\n    <choose>\n      <if type=\"article-journal article-magazine\" match=\"any\">\n        <group prefix=\", \" delimiter=\", \">\n          <group>\n            <text variable=\"volume\" font-style=\"italic\"/>\n            <text variable=\"issue\" prefix=\"(\" suffix=\")\"/>\n          </group>\n          <text variable=\"page\"/>\n        </group>\n      </if>\n      <else-if type=\"article-newspaper\">\n        <group delimiter=\" \" prefix=\", \">\n          <label variable=\"page\" form=\"short\"/>\n          <text variable=\"page\"/>\n        </group>\n      </else-if>\n      <else-if type=\"book graphic motion_picture report song chapter paper-conference\" match=\"any\">\n        <group prefix=\" (\" suffix=\")\" delimiter=\", \">\n          <text macro=\"edition\"/>\n          <group>\n            <text term=\"volume\" form=\"short\" plural=\"true\" text-case=\"capitalize-first\" suffix=\" \"/>\n            <number variable=\"number-of-volumes\" form=\"numeric\" prefix=\"1-\"/>\n          </group>\n          <group>\n            <text term=\"volume\" form=\"short\" text-case=\"capitalize-first\" suffix=\" \"/>\n            <number variable=\"volume\" form=\"numeric\"/>\n          </group>\n          <group>\n            <label variable=\"page\" form=\"short\" suffix=\" \"/>\n            <text variable=\"page\"/>\n          </group>\n        </group>\n      </else-if>\n      <else-if type=\"legal_case\">\n        <group prefix=\" (\" suffix=\")\" delimiter=\" \">\n          <text variable=\"authority\"/>\n          <date variable=\"issued\" form=\"text\"/>\n        </group>\n      </else-if>\n      <else-if type=\"bill legislation\" match=\"any\">\n        <date variable=\"issued\" prefix=\" (\" suffix=\")\">\n          <date-part name=\"year\"/>\n        </date>\n      </else-if>\n    </choose>\n  </macro>\n  <macro name=\"citation-locator\">\n    <group>\n      <choose>\n        <if locator=\"chapter\">\n          <label variable=\"locator\" form=\"long\" text-case=\"capitalize-first\"/>\n        </if>\n        <else>\n          <label variable=\"locator\" form=\"short\"/>\n        </else>\n      </choose>\n      <text variable=\"locator\" prefix=\" \"/>\n    </group>\n  </macro>\n  <macro name=\"container\">\n    <group>\n      <choose>\n        <if type=\"chapter paper-conference entry-encyclopedia\" match=\"any\">\n          <text term=\"in\" text-case=\"capitalize-first\" suffix=\" \"/>\n        </if>\n      </choose>\n      <text macro=\"container-contributors\"/>\n      <text macro=\"secondary-contributors\"/>\n      <text macro=\"container-title\"/>\n    </group>\n  </macro>\n  <macro name=\"container-title\">\n    <choose>\n      <if type=\"bill legal_case legislation\" match=\"none\">\n        <text variable=\"container-title\" font-style=\"italic\"/>\n      </if>\n      <else>\n        <group delimiter=\" \" prefix=\", \">\n          <choose>\n            <if variable=\"container-title\">\n              <text variable=\"volume\"/>\n              <text variable=\"container-title\"/>\n              <group delimiter=\" \">\n                <!--change to label variable=\"section\" as that becomes available -->\n                <text term=\"section\" form=\"symbol\"/>\n                <text variable=\"section\"/>\n              </group>\n              <text variable=\"page\"/>\n            </if>\n            <else>\n              <choose>\n                <if type=\"legal_case\">\n                  <text variable=\"number\" prefix=\"No. \"/>\n                </if>\n                <else>\n                  <text variable=\"number\" prefix=\"Pub. L. No. \"/>\n                  <group delimiter=\" \">\n                    <!--change to label variable=\"section\" as that becomes available -->\n                    <text term=\"section\" form=\"symbol\"/>\n                    <text variable=\"section\"/>\n                  </group>\n                </else>\n              </choose>\n            </else>\n          </choose>\n        </group>\n      </else>\n    </choose>\n  </macro>\n  <citation et-al-min=\"6\" et-al-use-first=\"1\" et-al-subsequent-min=\"3\" et-al-subsequent-use-first=\"1\" disambiguate-add-year-suffix=\"true\" disambiguate-add-names=\"true\" disambiguate-add-givenname=\"true\" collapse=\"year\" givenname-disambiguation-rule=\"primary-name\">\n    <sort>\n      <key macro=\"author\"/>\n      <key macro=\"issued-sort\"/>\n    </sort>\n    <layout prefix=\"(\" suffix=\")\" delimiter=\"; \">\n      <group delimiter=\", \">\n        <text macro=\"author-short\"/>\n        <text macro=\"issued-year\"/>\n        <text macro=\"citation-locator\"/>\n      </group>\n    </layout>\n  </citation>\n  <bibliography hanging-indent=\"true\" et-al-min=\"8\" et-al-use-first=\"6\" et-al-use-last=\"true\" entry-spacing=\"0\" line-spacing=\"2\">\n    <sort>\n      <key macro=\"author\"/>\n      <key macro=\"issued-sort\" sort=\"ascending\"/>\n    </sort>\n    <layout>\n      <group suffix=\".\">\n        <group delimiter=\". \">\n          <text macro=\"author\"/>\n          <text macro=\"issued\"/>\n          <text macro=\"title\" prefix=\" \"/>\n          <text macro=\"container\"/>\n        </group>\n        <text macro=\"locators\"/>\n        <group delimiter=\", \" prefix=\". \">\n          <text macro=\"event\"/>\n          <text macro=\"publisher\"/>\n        </group>\n      </group>\n      <text macro=\"access\" prefix=\" \"/>\n    </layout>\n  </bibliography>\n</style>\n','dbf5641fdccb32c20998f915ec97d24b15c378d7',0,0),('http://www.zotero.org/styles/apsa','American Political Science Association','apsa.csl','','The American Political Science Association style.','<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<style xmlns=\"http://purl.org/net/xbiblio/csl\" class=\"in-text\" version=\"1.0\" page-range-format=\"chicago\" demote-non-dropping-particle=\"sort-only\">\n  <!-- This style was edited with the Visual CSL Editor (http://steveridout.com/csl/visualEditor/) -->\n  <info>\n    <title>American Political Science Association</title>\n    <id>http://www.zotero.org/styles/apsa</id>\n    <link href=\"http://www.zotero.org/styles/apsa\" rel=\"self\"/>\n    <link href=\"http://www.apsanet.org/media/PDFs/Publications/APSAStyleManual2006.pdf\" rel=\"documentation\"/>\n    <author>\n      <name>Julian Onions</name>\n      <email>julian.onions@gmail.com</email>\n    </author>\n    <contributor>\n      <name>Sebastian Karcher</name>\n    </contributor>\n    <category citation-format=\"author-date\"/>\n    <category field=\"political_science\"/>\n    <summary>The American Political Science Association style.</summary>\n    <updated>2012-09-14T22:11:27+00:00</updated>\n    <rights license=\"http://creativecommons.org/licenses/by-sa/3.0/\">This work is licensed under a Creative Commons Attribution-ShareAlike 3.0 License</rights>\n  </info>\n  <locale xml:lang=\"en-US\">\n    <date form=\"text\">\n      <date-part name=\"month\" suffix=\" \"/>\n      <date-part name=\"day\" suffix=\", \"/>\n      <date-part name=\"year\"/>\n    </date>\n  </locale>\n  <locale xml:lang=\"nb-NO\">\n    <date form=\"text\">\n      <date-part name=\"day\" suffix=\" \"/>\n      <date-part name=\"month\" suffix=\" \"/>\n      <date-part name=\"year\"/>\n    </date>\n  </locale>\n  <locale xml:lang=\"nn-NO\">\n    <date form=\"text\">\n      <date-part name=\"day\" suffix=\" \"/>\n      <date-part name=\"month\" suffix=\" \"/>\n      <date-part name=\"year\"/>\n    </date>\n  </locale>\n  <macro name=\"editor\">\n    <names variable=\"editor\" delimiter=\", \">\n      <label form=\"short\" text-case=\"lowercase\" suffix=\". \" strip-periods=\"true\"/>\n      <name and=\"text\" delimiter=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"author\">\n    <names variable=\"author\">\n      <name name-as-sort-order=\"first\" and=\"text\" sort-separator=\", \" delimiter=\", \" delimiter-precedes-last=\"always\"/>\n      <label form=\"short\" prefix=\", \" suffix=\".\" text-case=\"lowercase\" strip-periods=\"true\"/>\n      <substitute>\n        <names variable=\"editor\"/>\n        <text macro=\"title\"/>\n      </substitute>\n    </names>\n  </macro>\n  <macro name=\"author-short\">\n    <names variable=\"author\">\n      <name form=\"short\" and=\"text\" delimiter=\", \" initialize-with=\". \" sort-separator=\", \"/>\n      <substitute>\n        <names variable=\"editor\"/>\n        <names variable=\"translator\"/>\n        <text macro=\"title\"/>\n      </substitute>\n    </names>\n  </macro>\n  <macro name=\"access\">\n    <choose>\n      <if type=\"legal_case\" match=\"none\">\n        <choose>\n          <if variable=\"URL\">\n            <group delimiter=\" \">\n              <text variable=\"URL\"/>\n              <group prefix=\"(\" suffix=\")\">\n                <date variable=\"accessed\" form=\"text\"/>\n              </group>\n            </group>\n          </if>\n        </choose>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"title\">\n    <choose>\n      <if type=\"bill book graphic legal_case legislation motion_picture report song\" match=\"any\">\n        <text variable=\"title\" font-style=\"italic\" text-case=\"title\"/>\n      </if>\n      <else>\n        <text variable=\"title\" quotes=\"true\" text-case=\"title\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"legal_case\">\n    <group prefix=\" \" delimiter=\" \">\n      <text variable=\"volume\"/>\n      <text variable=\"container-title\"/>\n    </group>\n    <text variable=\"authority\" prefix=\" (\" suffix=\")\"/>\n  </macro>\n  <macro name=\"publisher\">\n    <choose>\n      <if type=\"thesis\" match=\"none\">\n        <group delimiter=\": \">\n          <text variable=\"publisher-place\"/>\n          <text variable=\"publisher\"/>\n        </group>\n        <text variable=\"genre\" prefix=\". \"/>\n      </if>\n      <else>\n        <group delimiter=\". \">\n          <text variable=\"genre\"/>\n          <text variable=\"publisher\"/>\n        </group>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"year-date\">\n    <choose>\n      <if variable=\"issued\">\n        <date variable=\"issued\">\n          <date-part name=\"year\"/>\n        </date>\n      </if>\n      <else>\n        <text term=\"no date\" form=\"short\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"edition\">\n    <choose>\n      <if is-numeric=\"edition\">\n        <group delimiter=\" \">\n          <number variable=\"edition\" form=\"ordinal\"/>\n          <text term=\"edition\" form=\"short\" suffix=\".\" strip-periods=\"true\"/>\n        </group>\n      </if>\n      <else>\n        <text variable=\"edition\" suffix=\".\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"locator\">\n    <choose>\n      <if locator=\"page\">\n        <text variable=\"locator\"/>\n      </if>\n      <else>\n        <group delimiter=\" \">\n          <label variable=\"locator\" form=\"short\"/>\n          <text variable=\"locator\"/>\n        </group>\n      </else>\n    </choose>\n  </macro>\n  <citation et-al-min=\"4\" et-al-use-first=\"1\" disambiguate-add-year-suffix=\"true\" disambiguate-add-names=\"true\" disambiguate-add-givenname=\"true\" givenname-disambiguation-rule=\"primary-name\" collapse=\"year\">\n    <sort>\n      <key macro=\"author-short\"/>\n      <key macro=\"year-date\"/>\n    </sort>\n    <layout prefix=\"(\" suffix=\")\" delimiter=\"; \">\n      <group delimiter=\", \">\n        <group delimiter=\" \">\n          <text macro=\"author-short\"/>\n          <text macro=\"year-date\"/>\n        </group>\n        <text macro=\"locator\"/>\n      </group>\n    </layout>\n  </citation>\n  <bibliography hanging-indent=\"true\" subsequent-author-substitute=\"&#8212;&#8212;&#8212;\">\n    <sort>\n      <key macro=\"author\"/>\n      <key macro=\"year-date\"/>\n      <key variable=\"title\"/>\n    </sort>\n    <layout suffix=\".\">\n      <text macro=\"author\" suffix=\".\"/>\n      <date variable=\"issued\" prefix=\" \" suffix=\".\">\n        <date-part name=\"year\"/>\n      </date>\n      <choose>\n        <if type=\"bill book graphic legal_case legislation manuscript motion_picture report song thesis\" match=\"any\">\n          <text macro=\"legal_case\"/>\n          <group prefix=\" \" delimiter=\" \">\n            <text macro=\"title\" suffix=\".\"/>\n            <text macro=\"edition\"/>\n            <text macro=\"editor\" suffix=\".\"/>\n          </group>\n          <text prefix=\" \" suffix=\".\" macro=\"publisher\"/>\n        </if>\n        <else-if type=\"chapter paper-conference\" match=\"any\">\n          <text macro=\"title\" prefix=\" \" suffix=\".\"/>\n          <group prefix=\" \" delimiter=\" \">\n            <text term=\"in\" text-case=\"capitalize-first\"/>\n            <text variable=\"container-title\" font-style=\"italic\" suffix=\",\"/>\n            <text variable=\"collection-title\" suffix=\",\"/>\n            <text macro=\"editor\" suffix=\".\"/>\n            <group suffix=\".\">\n              <text macro=\"publisher\"/>\n              <text variable=\"page\" prefix=\", \"/>\n            </group>\n          </group>\n        </else-if>\n        <else>\n          <group prefix=\" \" delimiter=\" \" suffix=\".\">\n            <text macro=\"title\"/>\n            <text macro=\"editor\"/>\n          </group>\n          <choose>\n            <if type=\"speech\">\n              <group prefix=\" \" delimiter=\" \" suffix=\".\">\n                <text term=\"presented at\" text-case=\"capitalize-first\"/>\n                <group delimiter=\", \">\n                  <text variable=\"event\"/>\n                  <text variable=\"event-place\"/>\n                </group>\n              </group>\n            </if>\n          </choose>\n          <group prefix=\" \" suffix=\".\">\n            <text variable=\"container-title\" font-style=\"italic\"/>\n            <group prefix=\" \">\n              <text variable=\"volume\"/>\n              <text variable=\"issue\" prefix=\"(\" suffix=\")\"/>\n            </group>\n            <text variable=\"page\" prefix=\": \"/>\n          </group>\n        </else>\n      </choose>\n      <text prefix=\" \" macro=\"access\" suffix=\".\"/>\n    </layout>\n  </bibliography>\n</style>\n','ff3e914bf67def4ef48cd663c28893fdfedfc0af',0,0),('http://www.zotero.org/styles/asa','American Sociological Association','asa.csl','','The ASA style.','<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<style xmlns=\"http://purl.org/net/xbiblio/csl\" class=\"in-text\" version=\"1.0\" demote-non-dropping-particle=\"sort-only\" page-range-format=\"chicago\">\n  <info>\n    <title>American Sociological Association</title>\n    <id>http://www.zotero.org/styles/asa</id>\n    <link href=\"http://www.zotero.org/styles/asa\" rel=\"self\"/>\n    <link href=\"http://www.asanet.org/Quick%20Style%20Guide.pdf\" rel=\"documentation\"/>\n    <author>\n      <name>Julian Onions</name>\n      <email>julian.onions@gmail.com</email>\n    </author>\n    <category citation-format=\"author-date\"/>\n    <category field=\"sociology\"/>\n    <summary>The ASA style.</summary>\n    <updated>2012-09-27T22:06:38+00:00</updated>\n    <rights license=\"http://creativecommons.org/licenses/by-sa/3.0/\">This work is licensed under a Creative Commons Attribution-ShareAlike 3.0 License</rights>\n  </info>\n  <macro name=\"editor\">\n    <names variable=\"editor\">\n      <label form=\"verb\" suffix=\" \"/>\n      <name and=\"text\" delimiter=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"series-editor\">\n    <names variable=\"original-author\">\n      <label form=\"short\" text-case=\"capitalize-first\" suffix=\" \"/>\n      <name and=\"text\" delimiter=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"anon\">\n    <text term=\"anonymous\" form=\"short\" text-case=\"capitalize-first\" strip-periods=\"true\"/>\n  </macro>\n  <macro name=\"author\">\n    <names variable=\"author\">\n      <name and=\"text\" name-as-sort-order=\"first\" sort-separator=\", \" delimiter=\", \" delimiter-precedes-last=\"always\" initialize=\"false\" initialize-with=\". \"/>\n      <label form=\"short\" prefix=\", \"/>\n      <substitute>\n        <names variable=\"editor\"/>\n        <names variable=\"translator\"/>\n        <choose>\n          <if type=\"report\">\n            <text variable=\"publisher\"/>\n          </if>\n        </choose>\n        <text macro=\"anon\"/>\n      </substitute>\n    </names>\n  </macro>\n  <macro name=\"author-short\">\n    <names variable=\"author\">\n      <name form=\"short\" and=\"text\" delimiter=\", \" initialize-with=\". \"/>\n      <substitute>\n        <names variable=\"editor\"/>\n        <names variable=\"translator\"/>\n        <choose>\n          <if type=\"report\">\n            <text variable=\"publisher\"/>\n          </if>\n        </choose>\n        <text macro=\"anon\"/>\n      </substitute>\n    </names>\n  </macro>\n  <macro name=\"access\">\n    <group>\n      <choose>\n        <if variable=\"URL\">\n          <text term=\"retrieved\" text-case=\"capitalize-first\" suffix=\" \"/>\n          <date variable=\"accessed\">\n            <date-part name=\"month\" suffix=\" \"/>\n            <date-part name=\"day\" suffix=\", \"/>\n            <date-part name=\"year\"/>\n          </date>\n          <group prefix=\" (\" suffix=\")\">\n            <text variable=\"URL\"/>\n          </group>\n        </if>\n      </choose>\n    </group>\n  </macro>\n  <macro name=\"title\">\n    <choose>\n      <if type=\"thesis\">\n        <text variable=\"title\"/>\n      </if>\n      <else-if type=\"bill book graphic legal_case legislation motion_picture report song\" match=\"any\">\n        <text variable=\"title\" font-style=\"italic\"/>\n      </else-if>\n      <else>\n        <text variable=\"title\" quotes=\"true\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"publisher\">\n    <group delimiter=\": \">\n      <text variable=\"publisher-place\"/>\n      <text variable=\"publisher\"/>\n    </group>\n  </macro>\n  <macro name=\"year-date\">\n    <choose>\n      <if variable=\"issued\">\n        <date variable=\"issued\">\n          <date-part name=\"year\"/>\n        </date>\n      </if>\n      <else>\n        <text term=\"no date\" form=\"short\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"day-month\">\n    <date variable=\"issued\">\n      <date-part name=\"month\"/>\n      <date-part name=\"day\" prefix=\" \"/>\n    </date>\n  </macro>\n  <macro name=\"pages\">\n    <label variable=\"page\" form=\"short\" suffix=\" \" text-case=\"capitalize-first\"/>\n    <text variable=\"page\"/>\n  </macro>\n  <macro name=\"edition\">\n    <choose>\n      <if is-numeric=\"edition\">\n        <group delimiter=\" \">\n          <number variable=\"edition\" form=\"ordinal\"/>\n          <text term=\"edition\" form=\"short\"/>\n        </group>\n      </if>\n      <else>\n        <text variable=\"edition\" suffix=\".\"/>\n      </else>\n    </choose>\n  </macro>\n  <citation et-al-min=\"4\" et-al-use-first=\"1\" et-al-subsequent-min=\"3\" et-al-subsequent-use-first=\"1\" disambiguate-add-year-suffix=\"true\" disambiguate-add-names=\"true\" disambiguate-add-givenname=\"true\" collapse=\"year\">\n    <sort>\n      <key macro=\"author-short\"/>\n      <key macro=\"year-date\"/>\n    </sort>\n    <layout prefix=\"(\" suffix=\")\" delimiter=\"; \">\n      <group delimiter=\":\">\n        <group delimiter=\" \">\n          <text macro=\"author-short\"/>\n          <text macro=\"year-date\"/>\n        </group>\n        <text variable=\"locator\"/>\n      </group>\n    </layout>\n  </citation>\n  <bibliography hanging-indent=\"true\" et-al-min=\"6\" et-al-use-first=\"1\">\n    <sort>\n      <key macro=\"author\"/>\n      <key macro=\"year-date\"/>\n      <key variable=\"title\"/>\n    </sort>\n    <layout suffix=\".\">\n      <group delimiter=\" \">\n        <text macro=\"author\" suffix=\".\"/>\n        <text macro=\"year-date\" suffix=\".\"/>\n      </group>\n      <choose>\n        <if type=\"article-newspaper article-magazine\" match=\"any\">\n          <group delimiter=\" \">\n            <text macro=\"title\" prefix=\" \" suffix=\".\"/>\n          </group>\n          <group prefix=\" \" delimiter=\", \">\n            <text variable=\"container-title\" font-style=\"italic\"/>\n            <text macro=\"day-month\"/>\n            <text variable=\"edition\"/>\n            <text variable=\"page\"/>\n          </group>\n        </if>\n        <else-if type=\"thesis\">\n          <text macro=\"title\" prefix=\" \" suffix=\".\" quotes=\"true\"/>\n          <group prefix=\" \" delimiter=\", \">\n            <text macro=\"edition\"/>\n            <text macro=\"editor\" suffix=\".\"/>\n            <text variable=\"genre\"/>\n            <text macro=\"publisher\"/>\n          </group>\n        </else-if>\n        <else-if type=\"bill book graphic legal_case legislation motion_picture report song\" match=\"any\">\n          <group delimiter=\" \">\n            <text macro=\"title\" prefix=\" \" suffix=\".\"/>\n            <text macro=\"edition\"/>\n            <text macro=\"editor\" suffix=\".\"/>\n            <text macro=\"publisher\"/>\n          </group>\n        </else-if>\n        <else-if type=\"chapter paper-conference\" match=\"any\">\n          <group delimiter=\" \">\n            <text macro=\"title\" prefix=\" \" suffix=\".\"/>\n            <group delimiter=\", \" suffix=\".\">\n              <group delimiter=\" \">\n                <text macro=\"pages\"/>\n                <text term=\"in\" text-case=\"lowercase\"/>\n                <text variable=\"container-title\" font-style=\"italic\"/>\n              </group>\n              <text variable=\"volume\" prefix=\"vol. \"/>\n              <text variable=\"collection-title\" font-style=\"italic\"/>\n              <text macro=\"editor\"/>\n            </group>\n            <text macro=\"publisher\"/>\n          </group>\n        </else-if>\n        <else>\n          <group suffix=\".\">\n            <text macro=\"title\" prefix=\" \"/>\n            <text macro=\"editor\" prefix=\" \"/>\n          </group>\n          <group prefix=\" \" suffix=\".\" delimiter=\" \">\n            <text variable=\"container-title\" font-style=\"italic\"/>\n            <group delimiter=\":\">\n              <group>\n                <text variable=\"volume\"/>\n                <text variable=\"issue\" prefix=\"(\" suffix=\")\"/>\n              </group>\n              <text variable=\"page\"/>\n            </group>\n          </group>\n        </else>\n      </choose>\n      <text prefix=\". \" macro=\"access\"/>\n    </layout>\n  </bibliography>\n</style>\n','61b29a2c589cbbc4bea4f97bcb7f341861dae7e1',0,0),('http://www.zotero.org/styles/chicago-author-date','Chicago Manual of Style (author-date)','chicago-author-date.csl','','The author-date variant of the Chicago style','<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<style xmlns=\"http://purl.org/net/xbiblio/csl\" class=\"in-text\" version=\"1.0\" demote-non-dropping-particle=\"sort-only\">\n  <info>\n    <title>Chicago Manual of Style (author-date)</title>\n    <id>http://www.zotero.org/styles/chicago-author-date</id>\n    <link href=\"http://www.zotero.org/styles/chicago-author-date\" rel=\"self\"/>\n    <link href=\"http://www.chicagomanualofstyle.org/tools_citationguide.html\" rel=\"documentation\"/>\n    <author>\n      <name>Julian Onions</name>\n      <email>julian.onions@gmail.com</email>\n    </author>\n    <contributor>\n      <name>Sebastian Karcher</name>\n    </contributor>\n    <contributor>\n      <name>Richard Karnesky</name>\n      <email>karnesky+zotero@gmail.com</email>\n      <uri>http://arc.nucapt.northwestern.edu/Richard_Karnesky</uri>\n    </contributor>\n    <category citation-format=\"author-date\"/>\n    <category field=\"generic-base\"/>\n    <summary>The author-date variant of the Chicago style</summary>\n    <updated>2012-12-06T03:10:19+00:00</updated>\n    <rights license=\"http://creativecommons.org/licenses/by-sa/3.0/\">This work is licensed under a Creative Commons Attribution-ShareAlike 3.0 License</rights>\n  </info>\n  <locale>\n    <terms>\n      <term name=\"editor\" form=\"verb-short\">ed.</term>\n      <term name=\"container-author\" form=\"verb\">by</term>\n      <term name=\"translator\" form=\"verb-short\">trans.</term>\n    </terms>\n  </locale>\n  <macro name=\"secondary-contributors\">\n    <choose>\n      <if type=\"chapter paper-conference\" match=\"none\">\n        <group delimiter=\". \">\n          <choose>\n            <if variable=\"author\">\n              <names variable=\"editor\">\n                <label form=\"verb\" text-case=\"capitalize-first\" suffix=\" \" plural=\"never\"/>\n                <name and=\"text\" delimiter=\", \"/>\n              </names>\n            </if>\n          </choose>\n          <choose>\n            <if variable=\"author editor\" match=\"any\">\n              <names variable=\"translator\">\n                <label form=\"verb\" text-case=\"capitalize-first\" suffix=\" \" plural=\"never\"/>\n                <name and=\"text\" delimiter=\", \"/>\n              </names>\n            </if>\n          </choose>\n        </group>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"container-contributors\">\n    <choose>\n      <if type=\"chapter paper-conference\" match=\"any\">\n        <group prefix=\", \" delimiter=\", \">\n          <choose>\n            <if variable=\"author\">\n              <names variable=\"container-author editor\" delimiter=\", \">\n                <label form=\"verb\" suffix=\" \" plural=\"never\"/>\n                <name and=\"text\" delimiter=\", \"/>\n              </names>\n            </if>\n          </choose>\n          <choose>\n            <if variable=\"author editor\" match=\"any\">\n              <names variable=\"translator\">\n                <label form=\"short\" plural=\"never\" suffix=\" \"/>\n                <name and=\"text\" delimiter=\", \"/>\n              </names>\n            </if>\n          </choose>\n        </group>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"editor\">\n    <names variable=\"editor\">\n      <name name-as-sort-order=\"first\" and=\"text\" sort-separator=\", \" delimiter=\", \" delimiter-precedes-last=\"always\"/>\n      <label form=\"short\" prefix=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"translator\">\n    <names variable=\"translator\">\n      <name name-as-sort-order=\"first\" and=\"text\" sort-separator=\", \" delimiter=\", \" delimiter-precedes-last=\"always\"/>\n      <label form=\"short\" prefix=\", \" plural=\"never\"/>\n    </names>\n  </macro>\n  <macro name=\"recipient\">\n    <choose>\n      <if type=\"personal_communication\">\n        <choose>\n          <if variable=\"genre\">\n            <text variable=\"genre\" text-case=\"capitalize-first\"/>\n          </if>\n          <else>\n            <text term=\"letter\" text-case=\"capitalize-first\"/>\n          </else>\n        </choose>\n      </if>\n    </choose>\n    <names variable=\"recipient\" delimiter=\", \">\n      <label form=\"verb\" prefix=\" \" text-case=\"lowercase\" suffix=\" \"/>\n      <name and=\"text\" delimiter=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"contributors\">\n    <names variable=\"author\">\n      <name and=\"text\" name-as-sort-order=\"first\" sort-separator=\", \" delimiter=\", \" delimiter-precedes-last=\"always\"/>\n      <label form=\"short\" plural=\"never\" prefix=\", \"/>\n      <substitute>\n        <names variable=\"editor\"/>\n        <names variable=\"translator\"/>\n        <text macro=\"title\"/>\n      </substitute>\n    </names>\n    <text macro=\"recipient\"/>\n  </macro>\n  <macro name=\"contributors-short\">\n    <names variable=\"author\">\n      <name form=\"short\" and=\"text\" delimiter=\", \" initialize-with=\". \"/>\n      <substitute>\n        <names variable=\"editor\"/>\n        <names variable=\"translator\"/>\n        <text macro=\"title\"/>\n      </substitute>\n    </names>\n  </macro>\n  <macro name=\"interviewer\">\n    <names variable=\"interviewer\" delimiter=\", \">\n      <label form=\"verb\" prefix=\" \" text-case=\"capitalize-first\" suffix=\" \"/>\n      <name and=\"text\" delimiter=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"archive\">\n    <group delimiter=\". \">\n      <text variable=\"archive_location\" text-case=\"capitalize-first\"/>\n      <text variable=\"archive\"/>\n      <text variable=\"archive-place\"/>\n    </group>\n  </macro>\n  <macro name=\"access\">\n    <group delimiter=\". \">\n      <choose>\n        <if type=\"graphic report\" match=\"any\">\n          <text macro=\"archive\"/>\n        </if>\n        <else-if type=\"article-magazine article-newspaper bill book chapter graphic legal_case legislation motion_picture paper-conference report song thesis\" match=\"none\">\n          <text macro=\"archive\"/>\n        </else-if>\n      </choose>\n      <text variable=\"DOI\" prefix=\"doi:\"/>\n      <choose>\n        <if variable=\"DOI issued\" match=\"none\">\n          <choose>\n            <if variable=\"URL accessed\" match=\"all\">\n              <group delimiter=\" \">\n                <text term=\"accessed\" text-case=\"capitalize-first\"/>\n                <date variable=\"accessed\" delimiter=\" \">\n                  <date-part name=\"month\"/>\n                  <date-part name=\"day\"/>\n                </date>\n              </group>\n            </if>\n          </choose>\n        </if>\n        <else-if type=\"webpage\">\n          <date variable=\"issued\" delimiter=\" \">\n            <date-part name=\"month\"/>\n            <date-part name=\"day\"/>\n          </date>\n        </else-if>\n      </choose>\n      <choose>\n        <if type=\"legal_case\" match=\"none\">\n          <text variable=\"URL\"/>\n        </if>\n      </choose>\n    </group>\n  </macro>\n  <macro name=\"title\">\n    <choose>\n      <if variable=\"title\" match=\"none\">\n        <choose>\n          <if type=\"personal_communication\" match=\"none\">\n            <text variable=\"genre\" text-case=\"capitalize-first\"/>\n          </if>\n        </choose>\n      </if>\n      <else-if type=\"bill book graphic legal_case legislation motion_picture song\" match=\"any\">\n        <text variable=\"title\" text-case=\"title\" font-style=\"italic\"/>\n      </else-if>\n      <else>\n        <text variable=\"title\" text-case=\"title\" quotes=\"true\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"edition\">\n    <choose>\n      <if type=\"bill book graphic legal_case legislation motion_picture report song\" match=\"any\">\n        <choose>\n          <if is-numeric=\"edition\">\n            <group delimiter=\" \" prefix=\". \">\n              <number variable=\"edition\" form=\"ordinal\"/>\n              <text term=\"edition\" form=\"short\"/>\n            </group>\n          </if>\n          <else>\n            <text variable=\"edition\" prefix=\". \"/>\n          </else>\n        </choose>\n      </if>\n      <else-if type=\"chapter  paper-conference\" match=\"any\">\n        <choose>\n          <if is-numeric=\"edition\">\n            <group delimiter=\" \" prefix=\", \">\n              <number variable=\"edition\" form=\"ordinal\"/>\n              <text term=\"edition\" form=\"short\"/>\n            </group>\n          </if>\n          <else>\n            <text variable=\"edition\" prefix=\", \"/>\n          </else>\n        </choose>\n      </else-if>\n    </choose>\n  </macro>\n  <macro name=\"locators\">\n    <choose>\n      <if type=\"article-journal\">\n        <text variable=\"volume\" prefix=\" \"/>\n        <text variable=\"issue\" prefix=\" (\" suffix=\")\"/>\n      </if>\n      <else-if type=\"legal_case\">\n        <text variable=\"volume\" prefix=\", \"/>\n        <text variable=\"container-title\" prefix=\" \"/>\n        <text variable=\"page\" prefix=\" \"/>\n      </else-if>\n      <else-if type=\"bill book graphic legal_case legislation motion_picture report song\" match=\"any\">\n        <group prefix=\". \" delimiter=\". \">\n          <group>\n            <text term=\"volume\" form=\"short\" text-case=\"capitalize-first\" suffix=\" \"/>\n            <number variable=\"volume\" form=\"numeric\"/>\n          </group>\n          <group>\n            <number variable=\"number-of-volumes\" form=\"numeric\"/>\n            <text term=\"volume\" form=\"short\" prefix=\" \" plural=\"true\"/>\n          </group>\n        </group>\n      </else-if>\n      <else-if type=\"chapter paper-conference\" match=\"any\">\n        <choose>\n          <if variable=\"page\" match=\"none\">\n            <group prefix=\". \">\n              <text term=\"volume\" form=\"short\" text-case=\"capitalize-first\" suffix=\" \"/>\n              <number variable=\"volume\" form=\"numeric\"/>\n            </group>\n          </if>\n        </choose>\n      </else-if>\n    </choose>\n  </macro>\n  <macro name=\"locators-chapter\">\n    <choose>\n      <if type=\"chapter paper-conference\" match=\"any\">\n        <choose>\n          <if variable=\"page\">\n            <group prefix=\", \">\n              <text variable=\"volume\" suffix=\":\"/>\n              <text variable=\"page\"/>\n            </group>\n          </if>\n        </choose>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"locators-article\">\n    <choose>\n      <if type=\"article-newspaper\">\n        <group prefix=\", \" delimiter=\", \">\n          <group>\n            <text variable=\"edition\" suffix=\" \"/>\n            <text term=\"edition\" prefix=\" \"/>\n          </group>\n          <group>\n            <text term=\"section\" form=\"short\" suffix=\" \"/>\n            <text variable=\"section\"/>\n          </group>\n        </group>\n      </if>\n      <else-if type=\"article-journal\">\n        <text variable=\"page\" prefix=\": \"/>\n      </else-if>\n    </choose>\n  </macro>\n  <macro name=\"point-locators\">\n    <choose>\n      <if variable=\"locator\">\n        <choose>\n          <if locator=\"page\" match=\"none\">\n            <choose>\n              <if type=\"bill book graphic legal_case legislation motion_picture report song\" match=\"any\">\n                <choose>\n                  <if variable=\"volume\">\n                    <group>\n                      <text term=\"volume\" form=\"short\" suffix=\" \"/>\n                      <number variable=\"volume\" form=\"numeric\"/>\n                      <label variable=\"locator\" form=\"short\" prefix=\", \" suffix=\" \"/>\n                    </group>\n                  </if>\n                  <else>\n                    <label variable=\"locator\" form=\"short\" suffix=\" \"/>\n                  </else>\n                </choose>\n              </if>\n              <else>\n                <label variable=\"locator\" form=\"short\" suffix=\" \"/>\n              </else>\n            </choose>\n          </if>\n          <else-if type=\"bill book graphic legal_case legislation motion_picture report song\" match=\"any\">\n            <number variable=\"volume\" form=\"numeric\" suffix=\":\"/>\n          </else-if>\n        </choose>\n        <text variable=\"locator\"/>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"container-prefix\">\n    <text term=\"in\" text-case=\"capitalize-first\"/>\n  </macro>\n  <macro name=\"container-title\">\n    <choose>\n      <if type=\"chapter paper-conference\" match=\"any\">\n        <text macro=\"container-prefix\" suffix=\" \"/>\n      </if>\n    </choose>\n    <choose>\n      <if type=\"legal_case\" match=\"none\">\n        <text variable=\"container-title\" text-case=\"title\" font-style=\"italic\"/>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"publisher\">\n    <group delimiter=\": \">\n      <text variable=\"publisher-place\"/>\n      <text variable=\"publisher\"/>\n    </group>\n  </macro>\n  <macro name=\"date\">\n    <choose>\n      <if variable=\"issued\">\n        <date variable=\"issued\">\n          <date-part name=\"year\"/>\n        </date>\n      </if>\n      <else-if variable=\"accessed\">\n        <date variable=\"accessed\">\n          <date-part name=\"year\"/>\n        </date>\n      </else-if>\n    </choose>\n  </macro>\n  <macro name=\"day-month\">\n    <date variable=\"issued\">\n      <date-part name=\"month\"/>\n      <date-part name=\"day\" prefix=\" \"/>\n    </date>\n  </macro>\n  <macro name=\"collection-title\">\n    <text variable=\"collection-title\" text-case=\"title\"/>\n    <text variable=\"collection-number\" prefix=\" \"/>\n  </macro>\n  <macro name=\"event\">\n    <group>\n      <text term=\"presented at\" suffix=\" \"/>\n      <text variable=\"event\"/>\n    </group>\n  </macro>\n  <macro name=\"description\">\n    <choose>\n      <if type=\"interview\">\n        <group delimiter=\". \">\n          <text macro=\"interviewer\"/>\n          <text variable=\"medium\" text-case=\"capitalize-first\"/>\n        </group>\n      </if>\n      <else>\n        <text variable=\"medium\" text-case=\"capitalize-first\" prefix=\". \"/>\n      </else>\n    </choose>\n    <choose>\n      <if variable=\"title\" match=\"none\"/>\n      <else-if type=\"thesis\"/>\n      <else>\n        <group delimiter=\" \" prefix=\". \">\n          <text variable=\"genre\" text-case=\"capitalize-first\"/>\n          <choose>\n            <if type=\"report\">\n              <text variable=\"number\"/>\n            </if>\n          </choose>\n        </group>\n      </else>\n    </choose>\n    <!--This is for computer programs only. Localization new to 1.0.1, so may be missing in many locales-->\n    <group delimiter=\" \" prefix=\" (\" suffix=\")\">\n      <text term=\"version\"/>\n      <text variable=\"version\"/>\n    </group>\n  </macro>\n  <macro name=\"issue\">\n    <choose>\n      <if type=\"article-journal\">\n        <text macro=\"day-month\" prefix=\" (\" suffix=\")\"/>\n      </if>\n      <else-if type=\"legal_case\">\n        <text variable=\"authority\" prefix=\". \"/>\n      </else-if>\n      <else-if type=\"speech\">\n        <group prefix=\" \" delimiter=\", \">\n          <text macro=\"event\"/>\n          <text macro=\"day-month\"/>\n          <text variable=\"event-place\"/>\n        </group>\n      </else-if>\n      <else-if type=\"article-newspaper article-magazine\" match=\"any\">\n        <text macro=\"day-month\" prefix=\", \"/>\n      </else-if>\n      <else>\n        <group prefix=\". \" delimiter=\", \">\n          <choose>\n            <if type=\"thesis\">\n              <text variable=\"genre\" text-case=\"capitalize-first\"/>\n            </if>\n          </choose>\n          <text macro=\"publisher\"/>\n        </group>\n      </else>\n    </choose>\n  </macro>\n  <citation et-al-min=\"4\" et-al-use-first=\"1\" disambiguate-add-year-suffix=\"true\" disambiguate-add-names=\"true\" disambiguate-add-givenname=\"true\" givenname-disambiguation-rule=\"primary-name\">\n    <layout prefix=\"(\" suffix=\")\" delimiter=\"; \">\n      <group delimiter=\", \">\n        <group delimiter=\" \">\n          <text macro=\"contributors-short\"/>\n          <text macro=\"date\"/>\n        </group>\n        <text macro=\"point-locators\"/>\n      </group>\n    </layout>\n  </citation>\n  <bibliography hanging-indent=\"true\" et-al-min=\"11\" et-al-use-first=\"7\" subsequent-author-substitute=\"&#8212;&#8212;&#8212;\" entry-spacing=\"0\">\n    <sort>\n      <key macro=\"contributors\"/>\n      <key variable=\"issued\"/>\n    </sort>\n    <layout suffix=\".\">\n      <group delimiter=\". \">\n        <text macro=\"contributors\"/>\n        <text macro=\"date\"/>\n        <text macro=\"title\"/>\n      </group>\n      <text macro=\"description\"/>\n      <text macro=\"secondary-contributors\" prefix=\". \"/>\n      