ts>\n  </info>\n  <locale xml:lang=\"en\">\n    <terms>\n      <term name=\"editortranslator\" form=\"verb-short\">\n        <single>trans. and ed.</single>\n        <multiple>trans. and ed.</multiple>\n      </term>\n      <term name=\"editortranslator\" form=\"verb\">\n        <single>Translated and edited by</single>\n        <multiple>Translated and edited by</multiple>\n      </term>\n      <term name=\"editor\" form=\"verb-short\">ed.</term>\n      <term name=\"translator\" form=\"verb-short\">trans.</term>\n    </terms>\n  </locale>\n  <macro name=\"editor-translator\">\n    <group delimiter=\", \">\n      <choose>\n        <if variable=\"author\">\n          <group delimiter=\" \">\n            <choose>\n              <if variable=\"container-author\">\n                <group>\n                  <names variable=\"container-author\">\n                    <label form=\"verb-short\" text-case=\"lowercase\" suffix=\" \"/>\n                    <name and=\"text\" delimiter=\", \"/>\n                  </names>\n                </group>\n              </if>\n            </choose>\n          </group>\n          <names variable=\"editor translator\" delimiter=\", \">\n            <label form=\"verb-short\" text-case=\"lowercase\" suffix=\" \"/>\n            <name and=\"text\" delimiter=\", \"/>\n          </names>\n        </if>\n      </choose>\n    </group>\n  </macro>\n  <macro name=\"secondary-contributors-note\">\n    <choose>\n      <if type=\"chapter paper-conference\" match=\"none\">\n        <text macro=\"editor-translator\"/>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"container-contributors-note\">\n    <choose>\n      <if type=\"chapter paper-conference\" match=\"any\">\n        <text macro=\"editor-translator\"/>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"secondary-contributors\">\n    <choose>\n      <if type=\"chapter paper-conference\" match=\"none\">\n        <group delimiter=\". \">\n          <choose>\n            <if variable=\"author\">\n              <names variable=\"editor translator\" delimiter=\". \">\n                <label form=\"verb\" text-case=\"capitalize-first\" suffix=\" \"/>\n                <name and=\"text\" delimiter=\", \"/>\n              </names>\n            </if>\n          </choose>\n        </group>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"container-contributors\">\n    <choose>\n      <if type=\"chapter paper-conference\" match=\"any\">\n        <group delimiter=\", \">\n          <choose>\n            <if variable=\"author\">\n              <choose>\n                <if variable=\"container-author\">\n                  <names variable=\"container-author\">\n                    <label form=\"verb-short\" text-case=\"lowercase\" suffix=\" \"/>\n                    <name and=\"text\" delimiter=\", \"/>\n                  </names>\n                </if>\n              </choose>\n              <choose>\n                <if variable=\"container-author author\" match=\"all\">\n                  <group delimiter=\". \">\n                    <text variable=\"page\"/>\n                    <names variable=\"editor translator\" delimiter=\", \">\n                      <label form=\"verb\" suffix=\" \"/>\n                      <name and=\"text\" delimiter=\", \"/>\n                    </names>\n                  </group>\n                </if>\n                <else>\n                  <names variable=\"editor translator\" delimiter=\", \">\n                    <label form=\"verb\" text-case=\"lowercase\" suffix=\" \"/>\n                    <name and=\"text\" delimiter=\", \"/>\n                  </names>\n                </else>\n              </choose>\n            </if>\n          </choose>\n        </group>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"recipient-note\">\n    <names variable=\"recipient\" delimiter=\", \">\n      <label form=\"verb\" text-case=\"lowercase\" suffix=\" \"/>\n      <name and=\"text\" delimiter=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"contributors-note\">\n    <group delimiter=\" \">\n      <names variable=\"author\">\n        <name and=\"text\" sort-separator=\", \" delimiter=\", \"/>\n        <label form=\"short\" prefix=\", \"/>\n        <substitute>\n          <names variable=\"editor\"/>\n          <names variable=\"translator\"/>\n        </substitute>\n      </names>\n      <text macro=\"recipient-note\"/>\n    </group>\n  </macro>\n  <macro name=\"editor\">\n    <names variable=\"editor\">\n      <name name-as-sort-order=\"first\" and=\"text\" sort-separator=\", \" delimiter=\", \" delimiter-precedes-last=\"always\"/>\n      <label form=\"short\" prefix=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"translator\">\n    <names variable=\"translator\">\n      <name name-as-sort-order=\"first\" and=\"text\" sort-separator=\", \" delimiter=\", \" delimiter-precedes-last=\"always\"/>\n      <label form=\"verb-short\" prefix=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"recipient\">\n    <group delimiter=\" \">\n      <choose>\n        <if type=\"personal_communication\">\n          <choose>\n            <if variable=\"genre\">\n              <text variable=\"genre\" text-case=\"capitalize-first\"/>\n            </if>\n            <else>\n              <text term=\"letter\" text-case=\"capitalize-first\"/>\n            </else>\n          </choose>\n        </if>\n      </choose>\n      <text macro=\"recipient-note\"/>\n    </group>\n  </macro>\n  <macro name=\"contributors\">\n    <group delimiter=\". \">\n      <names variable=\"author\">\n        <name name-as-sort-order=\"first\" and=\"text\" sort-separator=\", \" delimiter=\", \" delimiter-precedes-last=\"always\"/>\n        <substitute>\n          <text macro=\"editor\"/>\n          <text macro=\"translator\"/>\n        </substitute>\n      </names>\n      <text macro=\"recipient\"/>\n    </group>\n  </macro>\n  <macro name=\"recipient-short\">\n    <names variable=\"recipient\">\n      <label form=\"verb\" text-case=\"lowercase\" suffix=\" \"/>\n      <name form=\"short\" and=\"text\" delimiter=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"contributors-short\">\n    <group delimiter=\" \">\n      <names variable=\"author\">\n        <name form=\"short\" and=\"text\" delimiter=\", \"/>\n        <substitute>\n          <names variable=\"editor\"/>\n          <names variable=\"translator\"/>\n        </substitute>\n      </names>\n      <text macro=\"recipient-short\"/>\n    </group>\n  </macro>\n  <macro name=\"contributors-sort\">\n    <names variable=\"author\">\n      <name name-as-sort-order=\"all\" and=\"text\" sort-separator=\", \" delimiter=\", \" delimiter-precedes-last=\"always\"/>\n      <substitute>\n        <names variable=\"editor\"/>\n        <names variable=\"translator\"/>\n        <text macro=\"title\"/>\n      </substitute>\n    </names>\n  </macro>\n  <macro name=\"interviewer-note\">\n    <names variable=\"interviewer\" delimiter=\", \">\n      <label form=\"verb\" text-case=\"lowercase\" suffix=\" \"/>\n      <name and=\"text\" delimiter=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"interviewer\">\n    <names variable=\"interviewer\" delimiter=\", \">\n      <label form=\"verb\" text-case=\"capitalize-first\" suffix=\" \"/>\n      <name and=\"text\" delimiter=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"title-note\">\n    <choose>\n      <if variable=\"title\" match=\"none\">\n        <text variable=\"genre\"/>\n      </if>\n      <else-if type=\"bill book graphic  legislation motion_picture report song\" match=\"any\">\n        <text variable=\"title\" text-case=\"title\" font-style=\"italic\"/>\n        <group delimiter=\" \" prefix=\", \">\n          <text term=\"version\"/>\n          <text variable=\"version\"/>\n        </group>\n      </else-if>\n      <else-if type=\"legal_case interview\" match=\"any\">\n        <text variable=\"title\"/>\n      </else-if>\n      <else>\n        <text variable=\"title\" text-case=\"title\" quotes=\"true\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"title\">\n    <choose>\n      <if variable=\"title\" match=\"none\">\n        <choose>\n          <if type=\"personal_communication\" match=\"none\">\n            <text variable=\"genre\" text-case=\"capitalize-first\"/>\n          </if>\n        </choose>\n      </if>\n      <else-if type=\"bill book graphic legislation motion_picture report song\" match=\"any\">\n        <text variable=\"title\" text-case=\"title\" font-style=\"italic\"/>\n        <group prefix=\" (\" suffix=\")\" delimiter=\" \">\n          <text term=\"version\"/>\n          <text variable=\"version\"/>\n        </group>\n      </else-if>\n      <else-if type=\"legal_case interview\" match=\"any\">\n        <text variable=\"title\"/>\n      </else-if>\n      <else>\n        <text variable=\"title\" text-case=\"title\" quotes=\"true\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"title-short\">\n    <choose>\n      <if variable=\"title\" match=\"none\">\n        <choose>\n          <if type=\"interview\">\n            <text term=\"interview\"/>\n          </if>\n          <else-if type=\"manuscript speech\" match=\"any\">\n            <text variable=\"genre\" form=\"short\"/>\n          </else-if>\n          <else-if type=\"personal_communication\">\n            <text macro=\"issued\"/>\n          </else-if>\n        </choose>\n      </if>\n      <else-if type=\"bill book graphic legislation motion_picture report song\" match=\"any\">\n        <text variable=\"title\" text-case=\"title\" form=\"short\" font-style=\"italic\"/>\n      </else-if>\n      <else-if type=\"legal_case interview\" match=\"any\">\n        <text variable=\"title\"/>\n      </else-if>\n      <else>\n        <text variable=\"title\" text-case=\"title\" form=\"short\" quotes=\"true\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"date-disambiguate\">\n    <choose>\n      <if disambiguate=\"true\">\n        <text macro=\"issued\"/>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"description-note\">\n    <group delimiter=\", \">\n      <text macro=\"interviewer-note\"/>\n      <text variable=\"medium\"/>\n      <choose>\n        <if variable=\"title\" match=\"none\"/>\n        <else-if type=\"thesis speech\" match=\"any\"/>\n        <else>\n          <text variable=\"genre\"/>\n        </else>\n      </choose>\n    </group>\n  </macro>\n  <macro name=\"description\">\n    <group delimiter=\", \">\n      <group delimiter=\". \">\n        <text macro=\"interviewer\"/>\n        <text variable=\"medium\" text-case=\"capitalize-first\"/>\n      </group>\n      <choose>\n        <if variable=\"title\" match=\"none\"/>\n        <else-if type=\"thesis speech\" match=\"any\"/>\n        <else>\n          <text variable=\"genre\" text-case=\"capitalize-first\"/>\n        </else>\n      </choose>\n    </group>\n  </macro>\n  <macro name=\"container-title-note\">\n    <group delimiter=\" \">\n      <choose>\n        <if type=\"chapter paper-conference\" match=\"any\">\n          <text term=\"in\" text-case=\"lowercase\"/>\n        </if>\n      </choose>\n      <choose>\n        <if type=\"legal_case\" match=\"none\">\n          <text variable=\"container-title\" text-case=\"title\" font-style=\"italic\"/>\n        </if>\n      </choose>\n    </group>\n  </macro>\n  <macro name=\"container-title\">\n    <group delimiter=\" \">\n      <choose>\n        <if type=\"chapter paper-conference\" match=\"any\">\n          <text term=\"in\" text-case=\"capitalize-first\"/>\n        </if>\n      </choose>\n      <choose>\n        <if type=\"legal_case\" match=\"none\">\n          <text variable=\"container-title\" text-case=\"title\" font-style=\"italic\"/>\n        </if>\n      </choose>\n    </group>\n  </macro>\n  <macro name=\"collection-title\">\n    <group delimiter=\" \">\n      <text variable=\"collection-title\" text-case=\"title\"/>\n      <text variable=\"collection-number\"/>\n    </group>\n  </macro>\n  <macro name=\"edition-note\">\n    <choose>\n      <if type=\"bill book chapter graphic legal_case legislation motion_picture paper-conference report song\" match=\"any\">\n        <choose>\n          <if is-numeric=\"edition\">\n            <group delimiter=\" \">\n              <number variable=\"edition\" form=\"ordinal\"/>\n              <text term=\"edition\" form=\"short\"/>\n            </group>\n          </if>\n          <else>\n            <number variable=\"edition\" suffix=\".\"/>\n          </else>\n        </choose>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"edition\">\n    <choose>\n      <if type=\"bill book chapter graphic legal_case legislation motion_picture paper-conference report song\" match=\"any\">\n        <choose>\n          <if is-numeric=\"edition\">\n            <group delimiter=\" \">\n              <number variable=\"edition\" form=\"ordinal\"/>\n              <text term=\"edition\" form=\"short\"/>\n            </group>\n          </if>\n          <else>\n            <number variable=\"edition\" text-case=\"capitalize-first\" suffix=\".\"/>\n          </else>\n        </choose>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"locators-note-join-with-space\">\n    <choose>\n      <if type=\"article-journal\">\n        <text macro=\"locators-note\"/>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"locators-note-join-with-comma\">\n    <choose>\n      <if type=\"article-journal\" match=\"none\">\n        <text macro=\"locators-note\"/>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"locators-note\">\n    <choose>\n      <if type=\"article-journal\">\n        <group delimiter=\", \">\n          <number variable=\"volume\"/>\n          <group delimiter=\" \">\n            <text term=\"issue\" form=\"short\"/>\n            <number variable=\"issue\"/>\n          </group>\n        </group>\n      </if>\n      <else-if type=\"legal_case\">\n        <group delimiter=\", \">\n          <group delimiter=\" \">\n            <number variable=\"volume\"/>\n            <text variable=\"container-title\"/>\n            <text variable=\"page\"/>\n          </group>\n          <text variable=\"locator\"/>\n        </group>\n      </else-if>\n      <else-if type=\"bill book chapter graphic legal_case legislation motion_picture paper-conference report song\" match=\"any\">\n        <group delimiter=\", \">\n          <group delimiter=\" \">\n            <text term=\"volume\" form=\"short\"/>\n            <number variable=\"volume\" form=\"numeric\"/>\n          </group>\n          <choose>\n            <if variable=\"locator\" match=\"none\">\n              <group delimiter=\" \">\n                <number variable=\"number-of-volumes\" form=\"numeric\"/>\n                <text term=\"volume\" form=\"short\" plural=\"true\"/>\n              </group>\n            </if>\n          </choose>\n          <text macro=\"edition-note\"/>\n        </group>\n      </else-if>\n    </choose>\n  </macro>\n  <macro name=\"locators-join-with-space\">\n    <choose>\n      <if type=\"article-journal\">\n        <text macro=\"locators\"/>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"locators-join-with-comma\">\n    <choose>\n      <if type=\"legal_case\">\n        <text macro=\"locators\"/>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"locators-join-with-period\">\n    <choose>\n      <if type=\"legal_case article-journal\" match=\"none\">\n        <text macro=\"locators\"/>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"locators\">\n    <choose>\n      <if type=\"article-journal\">\n        <group delimiter=\", \">\n          <number variable=\"volume\"/>\n          <group delimiter=\" \">\n            <text term=\"issue\" form=\"short\"/>\n            <number variable=\"issue\"/>\n          </group>\n        </group>\n      </if>\n      <else-if type=\"legal_case\">\n        <group delimiter=\" \">\n          <number variable=