   </if>\n        </choose>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"issue-join-with-comma\">\n    <choose>\n      <if type=\"article-journal legal_case\" match=\"none\">\n        <choose>\n          <if type=\"speech\" variable=\"publisher publisher-place\" match=\"none\">\n            <text macro=\"issue\"/>\n          </if>\n        </choose>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"issue\">\n    <choose>\n      <if type=\"article-journal legal_case\" match=\"any\">\n        <text macro=\"issued\" prefix=\"(\" suffix=\")\"/>\n      </if>\n      <else-if type=\"speech\">\n        <group delimiter=\", \">\n          <group delimiter=\" \">\n            <choose>\n              <if variable=\"title\" match=\"none\"/>\n              <else>\n                <text variable=\"genre\" text-case=\"capitalize-first\"/>\n              </else>\n            </choose>\n            <text macro=\"event\"/>\n          </group>\n          <text variable=\"event-place\"/>\n          <text macro=\"issued\"/>\n        </group>\n      </else-if>\n      <else-if type=\"article-newspaper\">\n        <text macro=\"issued\"/>\n      </else-if>\n      <else-if variable=\"publisher-place publisher\" match=\"any\">\n        <group delimiter=\", \">\n          <choose>\n            <if type=\"thesis\">\n              <text variable=\"genre\" text-case=\"capitalize-first\"/>\n            </if>\n          </choose>\n          <text macro=\"publisher\"/>\n          <text macro=\"issued\"/>\n        </group>\n      </else-if>\n      <else>\n        <text macro=\"issued\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"access-note\">\n    <group delimiter=\", \">\n      <choose>\n        <if type=\"graphic report\" match=\"any\">\n          <text macro=\"archive-note\"/>\n        </if>\n        <else-if type=\"article-journal article-magazine article-newspaper bill book chapter graphic legal_case legislation motion_picture paper-conference report song\" match=\"none\">\n          <text macro=\"archive-note\"/>\n        </else-if>\n      </choose>\n      <choose>\n        <if variable=\"issued\" match=\"none\">\n          <group delimiter=\" \">\n            <text term=\"accessed\"/>\n            <date variable=\"accessed\" form=\"text\"/>\n          </group>\n        </if>\n      </choose>\n      <choose>\n        <if type=\"legal_case\" match=\"none\">\n          <choose>\n            <if variable=\"DOI\">\n              <text variable=\"DOI\" prefix=\"doi:\"/>\n            </if>\n            <else>\n              <text variable=\"URL\"/>\n            </else>\n          </choose>\n        </if>\n      </choose>\n    </group>\n  </macro>\n  <macro name=\"access\">\n    <group delimiter=\". \">\n      <choose>\n        <if type=\"graphic report\" match=\"any\">\n          <text macro=\"archive\"/>\n        </if>\n        <else-if type=\"article-journal article-magazine article-newspaper bill book chapter graphic legal_case legislation motion_picture paper-conference report song\" match=\"none\">\n          <text macro=\"archive\"/>\n        </else-if>\n      </choose>\n      <choose>\n        <if variable=\"issued\" match=\"none\">\n          <group delimiter=\" \">\n            <text term=\"accessed\" text-case=\"capitalize-first\"/>\n            <date variable=\"accessed\" form=\"text\"/>\n          </group>\n        </if>\n      </choose>\n      <choose>\n        <if type=\"legal_case\" match=\"none\">\n          <choose>\n            <if variable=\"DOI\">\n              <text variable=\"DOI\" prefix=\"doi:\"/>\n            </if>\n            <else>\n              <text variable=\"URL\"/>\n            </else>\n          </choose>\n        </if>\n      </choose>\n    </group>\n  </macro>\n  <macro name=\"case-locator-subsequent\">\n    <choose>\n      <if type=\"legal_case\">\n        <text macro=\"locators-note\"/>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"case-issue-subsequent\">\n    <choose>\n      <if type=\"legal_case\">\n        <text macro=\"issue\"/>\n      </if>\n    </choose>\n  </macro>\n  <citation et-al-min=\"4\" et-al-use-first=\"1\" disambiguate-add-names=\"true\">\n    <layout suffix=\".\" delimiter=\"; \">\n      <choose>\n        <if position=\"ibid-with-locator\">\n          <group delimiter=\", \">\n            <text term=\"ibid\"/>\n            <text macro=\"point-locators-subsequent\"/>\n          </group>\n        </if>\n        <else-if position=\"ibid\">\n          <text term=\"ibid\"/>\n        </else-if>\n        <else-if position=\"subsequent\">\n          <group delimiter=\", \">\n            <text macro=\"contributors-short\"/>\n            <group delimiter=\" \">\n              <group delimiter=\", \">\n                <text macro=\"title-short\"/>\n                <text macro=\"date-disambiguate\"/>\n                <text macro=\"case-locator-subsequent\"/>\n              </group>\n              <text macro=\"case-issue-subsequent\"/>\n            </group>\n            <text macro=\"point-locators-subsequent\"/>\n          </group>\n        </else-if>\n        <else>\n          <group delimiter=\", \">\n            <group delimiter=\": \">\n              <group delimiter=\", \">\n                <group delimiter=\" \">\n                  <group delimiter=\", \">\n                    <group delimiter=\" \">\n                      <group delimiter=\", \">\n                        <group delimiter=\", \">\n                          <text macro=\"contributors-note\"/>\n                          <text macro=\"title-note\"/>\n                        </group>\n                        <text macro=\"description-note\"/>\n                        <text macro=\"secondary-contributors-note\"/>\n                        <text macro=\"container-title-note\"/>\n                        <text macro=\"container-contributors-note\"/>\n                      </group>\n                      <text macro=\"locators-note-join-with-space\"/>\n                    </group>\n                    <text macro=\"locators-note-join-with-comma\"/>\n                    <text macro=\"collection-title\"/>\n                    <text macro=\"issue-note-join-with-comma\"/>\n                  </group>\n                  <text macro=\"issue-note-join-with-space\"/>\n                </group>\n                <text macro=\"locators-newspaper\"/>\n                <text macro=\"point-locators-join-with-comma\"/>\n              </group>\n              <text macro=\"point-locators-join-with-colon\"/>\n            </group>\n            <text macro=\"access-note\"/>\n          </group>\n        </else>\n      </choose>\n    </layout>\n  </citation>\n  <bibliography hanging-indent=\"true\" et-al-min=\"11\" et-al-use-first=\"7\" subsequent-author-substitute=\"&#8212;&#8212;&#8212;\" entry-spacing=\"0\">\n    <sort>\n      <key macro=\"contributors-sort\"/>\n      <key variable=\"title\"/>\n      <key variable=\"genre\"/>\n      <key variable=\"issued\"/>\n    </sort>\n    <layout suffix=\".\">\n      <group delimiter=\". \">\n        <group delimiter=\": \">\n          <group delimiter=\", \">\n            <group delimiter=\" \">\n              <group delimiter=\". \">\n                <group delimiter=\" \">\n                  <group delimiter=\", \">\n                    <group delimiter=\". \">\n                      <group delimiter=\". \">\n                        <text macro=\"contributors\"/>\n                        <text macro=\"title\"/>\n                      </group>\n                      <text macro=\"description\"/>\n                      <text macro=\"secondary-contributors\"/>\n                      <group delimiter=\", \">\n                        <text macro=\"container-title\"/>\n                        <text macro=\"container-contributors\"/>\n                        <text macro=\"locators-chapter\"/>\n                      </group>\n                      <text macro=\"locators-join-with-period\"/>\n                    </group>\n                    <text macro=\"locators-join-with-comma\"/>\n                  </group>\n                  <text macro=\"locators-join-with-space\"/>\n                </group>\n                <text macro=\"collection-title\"/>\n                <text macro=\"issue-join-with-period\"/>\n              </group>\n              <text macro=\"issue-join-with-space\"/>\n            </group>\n            <text macro=\"issue-join-with-comma\"/>\n            <text macro=\"locators-newspaper\"/>\n          </group>\n          <text macro=\"locators-journal\"/>\n        </group>\n        <text macro=\"access\"/>\n      </group>\n    </layout>\n  </bibliography>\n</style>\n','8d7ecc577e82814deacadd829340ad7707aa45ff',0,0),('http://www.zotero.org/styles/chicago-note-bibliography','Chicago Manual of Style (note)','chicago-note-bibliography.csl','','Chicago format with short notes and bibliography','<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<style xmlns=\"http://purl.org/net/xbiblio/csl\" class=\"note\" version=\"1.0\" demote-non-dropping-particle=\"sort-only\">\n  <info>\n    <title>Chicago Manual of Style (note)</title>\n    <id>http://www.zotero.org/styles/chicago-note-bibliography</id>\n    <link href=\"http://www.zotero.org/styles/chicago-note-bibliography\" rel=\"self\"/>\n    <link href=\"http://www.chicagomanualofstyle.org/tools_citationguide.html\" rel=\"documentation\"/>\n    <author>\n      <name>Julian Onions</name>\n      <email>julian.onions@gmail.com</email>\n    </author>\n    <contributor>\n      <name>Simon Kornblith</name>\n      <email>simon@simonster.com</email>\n    </contributor>\n    <contributor>\n      <name>Elena Razlogova</name>\n      <email>elena.razlogova@gmail.com</email>\n    </contributor>\n    <contributor>\n      <name>Frank Bennett</name>\n      <email>biercenator@gmail.com</email>\n    </contributor>\n    <category citation-format=\"note\"/>\n    <category field=\"generic-base\"/>\n    <summary>Chicago format with short notes and bibliography</summary>\n    <updated>2012-10-25T21:15:26+00:00</updated>\n    <rights license=\"http://creativecommons.org/licenses/by-sa/3.0/\">This work is licensed under a Creative Commons Attribution-ShareAlike 3.0 License</rights>\n  </info>\n  <locale xml:lang=\"en\">\n    <terms>\n      <term name=\"editortranslator\" form=\"verb-short\">\n        <single>trans. and ed.</single>\n        <multiple>trans. and ed.</multiple>\n      </term>\n      <term name=\"editortranslator\" form=\"verb\">\n        <single>Translated and edited by</single>\n        <multiple>Translated and edited by</multiple>\n      </term>\n      <term name=\"editor\" form=\"verb-short\">ed.</term>\n      <term name=\"translator\" form=\"verb-short\">trans.</term>\n    </terms>\n  </locale>\n  <macro name=\"editor-translator\">\n    <group delimiter=\", \">\n      <choose>\n        <if variable=\"author\">\n          <group delimiter=\" \">\n            <choose>\n              <if variable=\"container-author\">\n                <group>\n                  <names variable=\"container-author\">\n                    <label form=\"verb-short\" text-case=\"lowercase\" suffix=\" \"/>\n                    <name and=\"text\" delimiter=\", \"/>\n                  </names>\n                </group>\n              </if>\n            </choose>\n          </group>\n          <names variable=\"editor translator\" delimiter=\", \">\n            <label form=\"verb-short\" text-case=\"lowercase\" suffix=\" \"/>\n            <name and=\"text\" delimiter=\", \"/>\n          </names>\n        </if>\n      </choose>\n    </group>\n  </macro>\n  <macro name=\"secondary-contributors-note\">\n    <choose>\n      <if type=\"chapter paper-conference\" match=\"none\">\n        <text macro=\"editor-translator\"/>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"container-contributors-note\">\n    <choose>\n      <if type=\"chapter paper-conference\" match=\"any\">\n        <text macro=\"editor-translator\"/>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"secondary-contributors\">\n    <choose>\n      <if type=\"chapter paper-conference\" match=\"none\">\n        <group delimiter=\". \">\n          <choose>\n            <if variable=\"author\">\n              <names variable=\"editor translator\" delimiter=\". \">\n                <label form=\"verb\" text-case=\"capitalize-first\" suffix=\" \"/>\n                <name and=\"text\" delimiter=\", \"/>\n              </names>\n            </if>\n          </choose>\n        </group>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"container-contributors\">\n    <choose>\n      <if type=\"chapter paper-conference\" match=\"any\">\n        <group delimiter=\", \">\n          <choose>\n            <if variable=\"author\">\n              <choose>\n                <if variable=\"container-author\">\n                  <names variable=\"container-author\">\n                    <label form=\"verb-short\" text-case=\"lowercase\" suffix=\" \"/>\n                    <name and=\"text\" delimiter=\", \"/>\n                  </names>\n                </if>\n              </choose>\n              <!--This includes page numers after the container author, e.g. for Introductions -->\n              <choose>\n                <if variable=\"container-author author\" match=\"all\">\n                  <group delimiter=\". \">\n                    <text variable=\"page\"/>\n                    <names variable=\"editor translator\" delimiter=\", \">\n                      <label form=\"verb\" suffix=\" \"/>\n                      <name and=\"text\" delimiter=\", \"/>\n                    </names>\n                  </group>\n                </if>\n                <else>\n                  <names variable=\"editor translator\" delimiter=\", \">\n                    <label form=\"verb\" text-case=\"lowercase\" suffix=\" \"/>\n                    <name and=\"text\" delimiter=\", \"/>\n                  </names>\n                </else>\n              </choose>\n            </if>\n          </choose>\n        </group>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"recipient-note\">\n    <names variable=\"recipient\" delimiter=\", \">\n      <label form=\"verb\" text-case=\"lowercase\" suffix=\" \"/>\n      <name and=\"text\" delimiter=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"contributors-note\">\n    <group delimiter=\" \">\n      <names variable=\"author\">\n        <name and=\"text\" sort-separator=\", \" delimiter=\", \"/>\n        <label form=\"short\" prefix=\", \"/>\n        <substitute>\n          <names variable=\"editor\"/>\n          <names variable=\"translator\"/>\n        </substitute>\n      </names>\n      <text macro=\"recipient-note\"/>\n    </group>\n  </macro>\n  <macro name=\"editor\">\n    <names variable=\"editor\">\n      <name name-as-sort-order=\"first\" and=\"text\" sort-separator=\", \" delimiter=\", \" delimiter-precedes-last=\"always\"/>\n      <label form=\"short\" prefix=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"translator\">\n    <names variable=\"translator\">\n      <name name-as-sort-order=\"first\" and=\"text\" sort-separator=\", \" delimiter=\", \" delimiter-precedes-last=\"always\"/>\n      <label form=\"verb-short\" prefix=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"recipient\">\n    <group delimiter=\" \">\n      <choose>\n        <if type=\"personal_communication\">\n          <choose>\n            <if variable=\"genre\">\n              <text variable=\"genre\" text-case=\"capitalize-first\"/>\n            </if>\n            <else>\n              <text term=\"letter\" text-case=\"capitalize-first\"/>\n            </else>\n          </choose>\n        </if>\n      </choose>\n      <text macro=\"recipient-note\"/>\n    </group>\n  </macro>\n  <macro name=\"contributors\">\n    <group delimiter=\". \">\n      <names variable=\"author\">\n        <name name-as-sort-order=\"first\" and=\"text\" sort-separator=\", \" delimi