      <text variable=\"volume\"/>\n              <text variable=\"issue\" prefix=\"(\" suffix=\")\"/>\n            </group>\n            <group prefix=\", \">\n              <label variable=\"page\" suffix=\".\" form=\"short\" strip-periods=\"true\"/>\n              <text variable=\"page\"/>\n            </group>\n          </group>\n        </else>\n      </choose>\n      <text prefix=\" \" macro=\"access\" suffix=\".\"/>\n    </layout>\n  </bibliography>\n</style>\n','7dae04093a74c902c7c206e63ac46c4a8be2d8a6',0,0),('http://www.zotero.org/styles/ieee','IEEE','ieee.csl','','','<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<style xmlns=\"http://purl.org/net/xbiblio/csl\" class=\"in-text\" version=\"1.0\" demote-non-dropping-particle=\"sort-only\">\n  <info>\n    <title>IEEE</title>\n    <id>http://www.zotero.org/styles/ieee</id>\n    <link href=\"http://www.zotero.org/styles/ieee\" rel=\"self\"/>\n    <link href=\"http://www.ieee.org/portal/cms_docs_iportals/iportals/publications/authors/transjnl/stylemanual.pdf\" rel=\"documentation\"/>\n    <link href=\"http://www.ieee.org/documents/auinfo07.pdf\" rel=\"documentation\"/>\n    <author>\n      <name>Michael Berkowitz</name>\n      <email>mberkowi@gmu.edu</email>\n    </author>\n    <contributor>\n      <name>Julian Onions</name>\n      <email>julian.onions@gmail.com</email>\n    </contributor>\n    <contributor>\n      <name>Rintze Zelle</name>\n      <uri>http://twitter.com/rintzezelle</uri>\n    </contributor>\n    <contributor>\n      <name>Stephen Frank</name>\n      <uri>http://www.zotero.org/sfrank</uri>\n    </contributor>\n    <contributor>\n      <name>Sebastian Karcher</name>\n    </contributor>\n    <category citation-format=\"numeric\"/>\n    <category field=\"engineering\"/>\n    <category field=\"generic-base\"/>\n    <updated>2012-10-18T22:38:43+00:00</updated>\n    <rights license=\"http://creativecommons.org/licenses/by-sa/3.0/\">This work is licensed under a Creative Commons Attribution-ShareAlike 3.0 License</rights>\n  </info>\n  <!-- Macros -->\n  <macro name=\"edition\">\n    <choose>\n      <if type=\"bill book chapter graphic legal_case legislation motion_picture paper-conference report song\" match=\"any\">\n        <choose>\n          <if is-numeric=\"edition\">\n            <group delimiter=\" \">\n              <number variable=\"edition\" form=\"ordinal\"/>\n              <text term=\"edition\" form=\"short\"/>\n            </group>\n          </if>\n          <else>\n            <text variable=\"edition\" text-case=\"capitalize-first\" suffix=\".\"/>\n          </else>\n        </choose>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"issued\">\n    <choose>\n      <if type=\"article-journal report\" match=\"any\">\n        <date variable=\"issued\">\n          <date-part name=\"month\" form=\"short\" suffix=\" \"/>\n          <date-part name=\"year\" form=\"long\"/>\n        </date>\n      </if>\n      <else-if type=\" bill book chapter graphic legal_case legislation motion_picture paper-conference song thesis\" match=\"any\">\n        <date variable=\"issued\">\n          <date-part name=\"year\" form=\"long\"/>\n        </date>\n      </else-if>\n      <else>\n        <date variable=\"issued\">\n          <date-part name=\"day\" form=\"numeric-leading-zeros\" suffix=\"-\"/>\n          <date-part name=\"month\" form=\"short\" suffix=\"-\" strip-periods=\"true\"/>\n          <date-part name=\"year\" form=\"long\"/>\n        </date>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"author\">\n    <names variable=\"author\">\n      <name initialize-with=\". \" delimiter=\", \" and=\"text\"/>\n      <label form=\"short\" prefix=\", \" text-case=\"capitalize-first\"/>\n      <substitute>\n        <names variable=\"editor\"/>\n        <names variable=\"translator\"/>\n      </substitute>\n    </names>\n  </macro>\n  <macro name=\"editor\">\n    <names variable=\"editor\">\n      <name initialize-with=\". \" delimiter=\", \" and=\"text\"/>\n      <label form=\"short\" prefix=\", \" text-case=\"capitalize-first\"/>\n    </names>\n  </macro>\n  <macro name=\"locators\">\n    <group delimiter=\", \">\n      <text macro=\"edition\"/>\n      <group delimiter=\" \">\n        <text term=\"volume\" form=\"short\"/>\n        <number variable=\"volume\" form=\"numeric\"/>\n      </group>\n      <group delimiter=\" \">\n        <number variable=\"number-of-volumes\" form=\"numeric\"/>\n        <text term=\"volume\" form=\"short\" plural=\"true\"/>\n      </group>\n      <group delimiter=\" \">\n        <text term=\"issue\" form=\"short\"/>\n        <number variable=\"issue\" form=\"numeric\"/>\n      </group>\n    </group>\n  </macro>\n  <macro name=\"title\">\n    <choose>\n      <if type=\"bill book graphic legal_case legislation motion_picture song\" match=\"any\">\n        <text variable=\"title\" font-style=\"italic\"/>\n      </if>\n      <else>\n        <text variable=\"title\" quotes=\"true\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"publisher\">\n    <choose>\n      <if type=\" bill book chapter graphic legal_case legislation motion_picture paper-conference song\" match=\"any\">\n        <text variable=\"publisher-place\" suffix=\": \"/>\n        <text variable=\"publisher\"/>\n      </if>\n      <else>\n        <group delimiter=\", \">\n          <text variable=\"publisher\"/>\n          <text variable=\"publisher-place\"/>\n        </group>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"event\">\n    <choose>\n      <if type=\"paper-conference\">\n        <choose>\n          <!-- Published Conference Paper -->\n          <if variable=\"container-title\">\n            <group delimiter=\", \">\n              <text variable=\"container-title\" prefix=\"in \" font-style=\"italic\"/>\n              <text variable=\"event-place\"/>\n            </group>\n          </if>\n          <!-- Unpublished Conference Paper -->\n          <else>\n            <group delimiter=\", \">\n              <text variable=\"event\" prefix=\"presented at the \"/>\n              <text variable=\"event-place\"/>\n            </group>\n          </else>\n        </choose>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"access\">\n    <choose>\n      <if type=\"webpage\">\n        <choose>\n          <if variable=\"URL\">\n            <group delimiter=\". \">\n              <text value=\"[Online]\"/>\n              <text variable=\"URL\" prefix=\"Available: \"/>\n              <group prefix=\"[\" suffix=\"]\">\n                <date variable=\"accessed\" prefix=\"Accessed: \">\n                  <date-part name=\"day\" form=\"numeric-leading-zeros\" suffix=\"-\"/>\n                  <date-part name=\"month\" form=\"short\" suffix=\"-\" strip-periods=\"true\"/>\n                  <date-part name=\"year\" form=\"long\"/>\n                </date>\n              </group>\n            </group>\n          </if>\n        </choose>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"page\">\n    <group>\n      <label variable=\"page\" form=\"short\" suffix=\" \"/>\n      <text variable=\"page\"/>\n    </group>\n  </macro>\n  <!-- Citation -->\n  <citation collapse=\"citation-number\">\n    <sort>\n      <key variable=\"citation-number\"/>\n    </sort>\n    <layout delimiter=\", \">\n      <text variable=\"citation-number\" prefix=\"[\" suffix=\"]\"/>\n    </layout>\n  </citation>\n  <!-- Bibliography -->\n  <bibliography entry-spacing=\"0\" second-field-align=\"flush\">\n    <layout suffix=\".\">\n      <!-- Citation Number -->\n      <text variable=\"citation-number\" prefix=\"[\" suffix=\"]\"/>\n      <!-- Author(s) -->\n      <text macro=\"author\" suffix=\", \"/>\n      <!-- Rest of Citation -->\n      <choose>\n        <!-- Specific Formats -->\n        <if type=\"article-journal\">\n          <group delimiter=\", \">\n            <text macro=\"title\"/>\n            <text variable=\"container-title\" font-style=\"italic\" form=\"short\"/>\n            <text macro=\"locators\"/>\n            <text macro=\"page\"/>\n            <text macro=\"issued\"/>\n          </group>\n        </if>\n        <else-if type=\"paper-conference\">\n          <group delimiter=\", \">\n            <text macro=\"title\"/>\n            <text macro=\"event\"/>\n            <text macro=\"issued\"/>\n            <text macro=\"locators\"/>\n            <text macro=\"page\"/>\n          </group>\n        </else-if>\n        <else-if type=\"report\">\n          <group delimiter=\", \">\n            <text macro=\"title\"/>\n            <text macro=\"publisher\"/>\n            <group delimiter=\" \">\n              <text variable=\"genre\"/>\n              <text variable=\"number\"/>\n            </group>\n            <text macro=\"issued\"/>\n          </group>\n        </else-if>\n        <else-if type=\"thesis\">\n          <group delimiter=\", \">\n            <text macro=\"title\"/>\n            <text variable=\"genre\"/>\n            <text macro=\"publisher\"/>\n            <text macro=\"issued\"/>\n          </group>\n        </else-if>\n        <else-if type=\"webpage\">\n          <group delimiter=\", \" suffix=\". \">\n            <text macro=\"title\"/>\n            <text variable=\"container-title\" font-style=\"italic\"/>\n            <text macro=\"issued\"/>\n          </group>\n          <text macro=\"access\"/>\n        </else-if>\n        <else-if type=\"patent\">\n          <text macro=\"title\" suffix=\", \"/>\n          <text variable=\"number\" prefix=\"U.S. Patent \"/>\n          <text macro=\"issued\"/>\n        </else-if>\n        <!-- Generic/Fallback Formats -->\n        <else-if type=\"bill book graphic legal_case legislation motion_picture report song\" match=\"any\">\n          <group delimiter=\", \" suffix=\". \">\n            <text macro=\"title\"/>\n            <text macro=\"locators\"/>\n          </group>\n          <group delimiter=\", \">\n            <text macro=\"publisher\"/>\n            <text macro=\"issued\"/>\n            <text macro=\"page\"/>\n          </group>\n        </else-if>\n        <else-if type=\"article-magazine article-newspaper broadcast interview manuscript map patent personal_communication song speech thesis webpage\" match=\"any\">\n          <group delimiter=\", \">\n            <text macro=\"title\"/>\n            <text variable=\"container-title\" font-style=\"italic\"/>\n            <text macro=\"locators\"/>\n            <text macro=\"publisher\"/>\n            <text macro=\"page\"/>\n            <text macro=\"issued\"/>\n          </group>\n        </else-if>\n        <else-if type=\"chapter paper-conference\" match=\"any\">\n          <group delimiter=\", \" suffix=\", \">\n            <text macro=\"title\"/>\n            <text variable=\"container-title\" prefix=\"in \" font-style=\"italic\"/>\n            <text macro=\"locators\"/>\n          </group>\n          <text macro=\"editor\" suffix=\" \"/>\n          <group delimiter=\", \">\n            <text macro=\"publisher\"/>\n            <text macro=\"issued\"/>\n            <text macro=\"page\"/>\n          </group>\n        </else-if>\n        <else>\n          <group delimiter=\", \" suffix=\". \">\n            <text macro=\"title\"/>\n            <text variable=\"container-title\" font-style=\"italic\"/>\n            <text macro=\"locators\"/>\n          </group>\n          <group delimiter=\", \">\n            <text macro=\"publisher\"/>\n            <text macro=\"page\"/>\n            <text macro=\"issued\"/>\n          </group>\n        </else>\n      </choose>\n    </layout>\n  </bibliography>\n</style>\n','89026ee4ddc82e1d5ffd320f64cb2d9558c1c6d5',0,0),('http://www.zotero.org/styles/mhra','Modern Humanities Research Association (note with bibliography)','mhra.csl','','MHRA format with full notes and bibliography','<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<style xmlns=\"http://purl.org/net/xbiblio/csl\" class=\"note\" version=\"1.0\" demote-non-dropping-particle=\"sort-only\" default-locale=\"en-GB\">\n  <info>\n    <title>Modern Humanities Research Association (note with bibliography)</title>\n    <id>http://www.zotero.org/styles/mhra</id>\n    <link href=\"http://www.zotero.org/styles/mhra\" rel=\"self\"/>\n    <link href=\"http://www.mhra.org.uk/Publications/Books/StyleGuide/download.shtml\" rel=\"documentation\"/>\n    <author>\n      <name>Rintze Zelle</name>\n      <uri>http://twitter.com/rintzezelle</uri>\n    </author>\n    <contributor>\n      <name>Sebastian Karcher</name>\n    </contributor>\n    <category citation-format=\"note\"/>\n    <category field=\"generic-base\"/>\n    <summary>MHRA format with full notes and bibliography</summary>\n    <updated>2012-09-27T22:06:38+00:00</updated>\n    <rights license=\"http://creativecommons.org/licenses/by-sa/3.0/\">This work is licensed under a Creative Commons Attribution-ShareAlike 3.0 License</rights>\n  </info>\n  <locale xml:lang=\"en\">\n    <terms>\n      <term name=\"et-al\">and others</term>\n      <term name=\"editor\" form=\"verb-short\">ed. by</term>\n      <term name=\"edition\" form=\"short\">edn</term>\n      <term name=\"translator\" form=\"verb-short\">trans. by</term>\n    </terms>\n  </locale>\n  <macro name=\"author\">\n    <names variable=\"author\">\n      <name name-as-sort-order=\"first\" and=\"text\" sort-separator=\", \" delimiter=\", \" delimiter-precedes-last=\"always\"/>\n      <label form=\"short\" prefix=\", \" suffix=\".\"/>\n      <substitute>\n        <names variable=\"editor\"/>\n        <names variable=\"translator\"/>\n        <text macro=\"title-note\"/>\n      </substitute>\n    </names>\n  </macro>\n  <macro name=\"contributors-note\">\n    <names variable=\"author\">\n      <name and=\"text\" sort-separator=\", \" delimiter=\", \" delimiter-precedes-last=\"never\"/>\n    </names>\n    <text macro=\"recipient-note\"/>\n  </macro>\n  <macro name=\"title-note\">\n    <choose>\n      <if type=\"bill book graphic legal_case legislation motion_picture report song\" match=\"any\">\n        <text variable=\"title\" font-style=\"italic\" text-case=\"title\"/>\n      </if>\n      <else>\n        <text variable=\"title\" quotes=\"true\" text-case=\"title\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"title-short\">\n    <choose>\n      <if disambiguate=\"true\">\n        <choose>\n          <if type=\"bill book graphic legal_case legislation motion_picture report song\" match=\"any\">\n            <text variable=\"title\" font-style=\"italic\" text-case=\"title\" form=\"short\"/>\n          </if>\n          <else>\n            <text variable=\"title\" quotes=\"true\" text-case=\"title\" form=\"short\"/>\n          </else>\n        </choose>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"title-sort-substitute\">\n    <choose>\n      <if type=\"bill book graphic legal_case legislation motion_picture report song\" match=\"any\">\n        <text variable=\"title\" font-style=\"italic\" text-case=\"title\" form=\"short\"/>\n      </if>\n      <else>\n        <text variable=\"title\" quotes=\"true\" text-case=\"title\" form=\"short\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"editor-translator\">\n    <group delimiter=\", \">\n      <names variable=\"editor\" delimiter=\", \">\n        <label form=\"verb-short\" text-case=\"lowercase\" suffix=\" \"/>\n        <name and=\"text\" delimiter=\", \" delimiter-precedes-last=\"never\"/>\n      </names>\n      <choose>\n        <if variable=\"author editor\" match=\"any\">\n          <names variable=\"translator\" delimiter=\", \">\n            <label form=\"verb-short\" text-case=\"lowercase\" suffix=\" \"/>\n            <name and=\"text\" delimiter=\", \" delimiter-precedes-last=\"never\"/>\n          </names>\n        </if>\n      </choose>\n    </group>\n  </macro>\n  <macro name=\"collection-title\">\n    <text variable=\"collection-title\" text-case=\"title\"/>\n    <text variable=\"collection-number\" prefix=\", \"/>\n  </macro>\n  <macro name=\"locators-note\">\n    <choose>\n      <if type=\"article-journal\">\n        <text variable=\"volume\"/>\n      </if>\n      <else-if type=\"bill book chapter graphic legal_case legislation motion_picture paper-conference report song\" match=\"any\">\n        <group delimiter=\", \">\n          <text macro=\"edition-note\"/>\n          <group>\n            <number variable=\"number-of-volumes\" form=\"numeric\"/>\n            <text term=\"volume\" form=\"short\" prefix=\" \" plural=\"true\"/>\n          </group>\n        </group>\n      </else-if>\n    </choose>\n  </macro>\n  <macro name=\"volume\">\n    <choose>\n      <if type=\"article-journal\">\n        <text variable=\"volume\"/>\n      </if>\n      <else-if type=\"bill book chapter graphic legal_case legislation motion_picture paper-conference report song\" match=\"any\">\n        <group delimiter=\", \">\n          <text macro=\"edition-note\"/>\n          <group>\n            <number variable=\"number-of-volumes\" form=\"numeric\"/>\n            <text term=\"volume\" form=\"short\" prefix=\" \" plural=\"true\"/>\n          </group>\n        </group>\n      </else-if>\n    </choose>\n  </macro>\n  <macro name=\"issue-note\">\n    <choose>\n      <if type=\"article-journal\">\n        <choose>\n          <if variable=\"volume\">\n            <text macro=\"issued\" prefix=\" (\" suffix=\")\"/>\n          </if>\n          <else>\n            <text macro=\"issued\" prefix=\", \"/>\n          </else>\n        </choose>\n      </if>\n      <else-if variable=\"publisher-place publisher\" match=\"any\">\n        <group prefix=\" (\" suffix=\")\" delimiter=\", \">\n          <group delimiter=\" \">\n            <choose>\n              <if variable=\"title\" match=\"none\"/>\n              <else-if type=\"thesis speech\" match=\"any\">\n                <text variable=\"genre\" prefix=\"unpublished \"/>\n              </else-if>\n            </choose>\n            <text macro=\"event\"/>\n          </group>\n          <text macro=\"publisher\"/>\n          <text macro=\"issued\"/>\n        </group>\n      </else-if>\n      <else>\n        <text macro=\"issued\" prefix=\", \"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"locators-specific-note\">\n    <choose>\n      <if type=\"bill book chapter graphic legal_case legislation motion_picture paper-conference report song\" match=\"any\">\n        <choose>\n          <if is-numeric=\"volume\">\n            <number variable=\"volume\" form=\"roman\" font-variant=\"small-caps\"/>\n          </if>\n        </choose>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"container-title-note\">\n    <choose>\n      <if type=\"chapter paper-conference\" match=\"any\">\n        <text term=\"in\" text-case=\"lowercase\" suffix=\" \"/>\n      </if>\n    </choose>\n    <text variable=\"container-title\" font-style=\"italic\"/>\n  </macro>\n  <macro name=\"edition-note\">\n    <choose>\n      <if type=\"bill book chapter graphic legal_case legislation motion_picture paper-conference report song\" match=\"any\">\n        <choose>\n          <if is-numeric=\"edition\">\n            <group delimiter=\" \">\n              <number variable=\"edition\" form=\"ordinal\"/>\n              <text term=\"edition\" form=\"short\"/>\n            </group>\n          </if>\n          <else>\n            <text variable=\"edition\"/>\n          </else>\n        </choose>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"editor-note\">\n    <names variable=\"editor\">\n      <name and=\"text\" sort-separator=\", \" delimiter=\", \"/>\n      <label form=\"short\" prefix=\", \" suffix=\".\"/>\n    </names>\n  </macro>\n  <macro name=\"translator-note\">\n    <names variable=\"translator\">\n      <name and=\"text\" sort-separator=\", \" delimiter=\", \"/>\n      <label form=\"verb-short\" prefix=\", \" suffix=\".\"/>\n    </names>\n  </macro>\n  <macro name=\"recipient-note\">\n    <names variable=\"recipient\" delimiter=\", \">\n      <label form=\"verb\" prefix=\" \" text-case=\"lowercase\" suffix=\" \"/>\n      <name and=\"text\" delimiter=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"recipient-short\">\n    <names variable=\"recipient\">\n      <label form=\"verb\" prefix=\" \" text-case=\"lowercase\" suffix=\" \"/>\n      <name form=\"short\" and=\"text\" delimiter=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"contributors-short\">\n    <names variable=\"author\">\n      <name form=\"short\" and=\"text\" sort-separator=\", \" delimiter=\", \" delimiter-precedes-last=\"never\"/>\n      <substitute>\n        <names variable=\"editor\"/>\n        <names variable=\"translator\"/>\n        <text macro=\"title-sort-substitute\"/>\n      </substitute>\n    </names>\n    <text macro=\"recipient-short\"/>\n  </macro>\n  <macro name=\"interviewer-note\">\n    <names variable=\"interviewer\" delimiter=\", \">\n      <label form=\"verb\" prefix=\" \" text-case=\"lowercase\" suffix=\" \"/>\n      <name and=\"text\" delimiter=\", \"/>\n    </names>\n  </macro>\n  <macro name=\"locators-newspaper\">\n    <choose>\n      <if type=\"article-newspaper\">\n        <group delimiter=\", \">\n          <group>\n            <text variable=\"edition\" suffix=\" \"/>\n            <text term=\"edition\" prefix=\" \"/>\n          </group>\n          <group>\n            <text term=\"section\" suffix=\" \"/>\n            <text variable=\"section\"/>\n          </group>\n        </group>\n      </if>\n    </choose>\n  </macro>\n  <macro name=\"event\">\n    <group>\n      <text term=\"presented at\" suffix=\" \"/>\n      <text variable=\"event\"/>\n    </group>\n  </macro>\n  <macro name=\"publisher\">\n    <group delimiter=\": \">\n      <text variable=\"publisher-place\"/>\n      <text variable=\"publisher\"/>\n    </group>\n  </macro>\n  <macro name=\"issued\">\n    <choose>\n      <if type=\"graphic report article-newspaper\" match=\"any\">\n        <date variable=\"issued\">\n          <date-part name=\"day\" suffix=\" \"/>\n          <date-part name=\"month\" suffix=\" \"/>\n          <date-part name=\"year\"/>\n        </date>\n      </if>\n      <else-if type=\"bill book chapter graphic legal_case legislation motion_picture paper-conference report song thesis\" match=\"any\">\n        <date variable=\"issued\">\n          <date-part name=\"year\"/>\n        </date>\n      </else-if>\n      <else>\n        <date variable=\"issued\">\n          <date-part name=\"year\"/>\n        </date>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"pages\">\n    <choose>\n      <if type=\"article-journal\">\n        <text variable=\"page\" prefix=\", \"/>\n      </if>\n      <else>\n        <choose>\n          <if variable=\"volume\">\n            <text variable=\"page\" prefix=\", \"/>\n          </if>\n          <else>\n            <label variable=\"page\" form=\"short\" prefix=\", \" suffix=\" \"/>\n            <text variable=\"page\"/>\n          </else>\n        </choose>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"point-locators\">\n    <text macro=\"pages\"/>\n    <choose>\n      <if variable=\"page\">\n        <group prefix=\" (\" suffix=\")\">\n          <label variable=\"locator\" form=\"short\" suffix=\" \"/>\n          <text variable=\"locator\"/>\n        </group>\n      </if>\n      <else>\n        <label variable=\"locator\" form=\"short\" prefix=\", \" suffix=\" \"/>\n        <text variable=\"locator\"/>\n      </else>\n    </choose>\n  </macro>\n  <macro name=\"point-locators-subsequent\">\n    <label variable=\"locator\" form=\"short\" prefix=\", \" suffix=\" \"/>\n    <text variable=\"locator\"/>\n  </macro>\n  <macro name=\"archive-note\">\n    <group delimiter=\", \">\n      <text variable=\"archive_location\"/>\n      <text variable=\"archive\"/>\n      <text variable=\"archive-place\"/>\n    </group>\n  </macro>\n  <macro name=\"access-note\">\n    <group delimiter=\", \">\n      <choose>\n        <if type=\"graphic report\" match=\"any\">\n          <text macro=\"archive-note\" prefix=\", \"/>\n        </if>\n        <else-if type=\"article-journal article-magazine article-newspaper bill book chapter graphic legal_case legislation motion_picture paper-conference report song thesis\" match=\"none\">\n          <text macro=\"archive-note\" prefix=\", \"/>\n        </else-if>\n      </choose>\n    </group>\n    <choose>\n      <if variable=\"DOI\">\n        <text variable=\"DOI\" prefix=\" &lt;doi:\" suffix=\"&gt;\"/>\n      </if>\n      <else>\n        <choose>\n          <if variable=\"URL\">\n            <text variable=\"URL\" prefix=\" &lt;\" suffix=\"&gt;\"/>\n            <group prefix=\" [\" suffix=\"]\">\n              <text term=\"accessed\" text-case=\"lowercase\"/>\n              <date variable=\"accessed\">\n                <date-part name=\"day\" prefix=\" \"/>\n                <date-part name=\"month\" prefix=\" \"/>\n                <date-part name=\"year\" prefix=\" \"/>\n              </date>\n            </group>\n          </if>\n        </choose>\n      </else>\n    </choose>\n  </macro>\n  <citation et-al-min=\"4\" et-al-use-first=\"1\" disambiguate-add-names=\"true\" disambiguate-add-givenname=\"true\">\n    <layout prefix=\"\" suffix=\".\" delimiter=\"; \">\n      <choose>\n        <if position=\"subsequent\">\n          <group delimiter=\", \">\n            <text macro=\"contributors-short\"/>\n            <text macro=\"title-short\"/>\n            <text macro=\"locators-specific-note\"/>\n          </group>\n          <text macro=\"point-locators-subsequent\"/>\n        </if>\n        <else>\n          <group delimiter=\", \">\n            <text macro=\"contributors-note\"/>\n            <text macro=\"title-note\"/>\n            <text macro=\"container-title-note\"/>\n            <text macro=\"editor-translator\"/>\n            <text macro=\"collection-title\"/>\n            <text macro=\"locators-note\"/>\n          </group>\n          <text mac