INSERT INTO `field_data_body` VALUES ('node','page',0,2,2,'und',0,'<h1>PI</h1>\r\n\r\n<p>Ross bio</p>\r\n\r\n<h1>PhD Students</h1>\r\n\r\n<h2>Christoforos (Chris) Mavrogiannis<img alt=\"\" height=\"175\" src=\"/sites/default/files/pictures/christoforos.JPG\" style=\"float:right\" width=\"180\" /></h2>\r\n\r\n<p class=\"rteindent1\">Chris received his Diploma degree (equivalent to MSc) in Mechanical Engineering from the National Technical University of Athens, Greece in 2013. He is currently pursuing a PhD in Mechanical engineering, working on the development of motion planning algorithms&nbsp;for&nbsp;seamless navigation of pedestrian environments. In the past he has done research on robotic grasping and robotic hand design.</p>\r\n\r\n<p>Jon Bio</p>\r\n\r\n<p>Wil Bio</p>\r\n\r\n<h1>Master\'s Students</h1>\r\n\r\n<p>Do we have any?</p>\r\n\r\n<h1>Undergraduate Students</h1>\r\n\r\n<h2>Daryl Sew<img alt=\"\" height=\"175\" src=\"/sites/default/files/pictures/daryl.jpg\" style=\"float:right\" width=\"157\" /></h2>\r\n\r\n<p class=\"rteindent1\">Computer Science undergraduate interested in computer vision, machine learning and robotics.</p>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h2>Minae Kwon<img alt=\"\" height=\"175\" src=\"/sites/default/files/pictures/minae.jpg\" style=\"float:right\" width=\"131\" /></h2>\r\n\r\n<p class=\"rteindent1\">I’m interested in facilitating collaboration between robots and humans by&nbsp;computationally expressing findings from social and cognitive psychology to model human behavior and appropriately design robot behavior. Currently, I am working on how humans form expectations of social robots and how we can help people form more accurate mental models of them.&nbsp; When not doing research, I enjoy debating (competitively), watching movies, and traveling.</p>\r\n\r\n<h1>&nbsp;</h1>\r\n\r\n<h1>Lab Alumni</h1>\r\n\r\n<h2>Alex Volkov<img alt=\"\" height=\"175\" src=\"/sites/default/files/pictures/alex.png\" style=\"float:right\" width=\"134\" /></h2>\r\n\r\n<p class=\"rteindent1\">ECE undergraduate, moved on to a Master\'s at the CMU Robotics Institute.</p>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h2>Vitchyr Pong</h2>\r\n\r\n<p class=\"rteindent1\">Undergraduate in ECE and CS, moved on to a PhD at UC Berkeley.<img alt=\"\" height=\"175\" src=\"/sites/default/files/pictures/vitchyr.jpg\" style=\"float:right\" width=\"263\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n','','full_html'),('node','page',0,3,3,'und',0,'<p>Publications to go here.</p>\r\n','','filtered_html'),('node','page',0,4,4,'und',0,'<p>Links to project pages to go here</p>\r\n','','filtered_html'),('node','news',0,6,6,'und',0,'<p>This is a fake new story about exciting things the lab has accomplished. Real content will go here once the formatting is done.</p>\r\n','','full_html'),('node','page',0,7,7,'und',0,'<h1 class=\"rtecenter\">Cornell Robotic Personal Assistants Lab</h1>\r\n\r\n<p class=\"rtecenter\">The Cornell Robotic Personal Assistants Lab is focused on advancing the state of the art&nbsp;in HRI, robotic manipulation, and multi-agent planning and coordination.</p>\r\n','','full_html');
